/// <summary>
/// Returns the list of available countries for the customer credit note for settlement discount functionality.
/// </summary>
/// <param name="_includeCZ">
/// Includes CZ context to the list of countries.
/// </param>
/// <returns>List of available countries.</returns>
public static container listOfCountriesForCreditNoteCashDisc(NoYes _includeCZ = NoYes::No)
{
    #isoCountryRegionCodes

    container availableCountriesCon = [#isoDK, #isoGB, #isoIE, #isoNL, #isoDE, #isoFR, #isoIS];

    if (_includeCZ)
    {
        availableCountriesCon += [#isoCZ];
    }

    return availableCountriesCon;
}

/// <summary>
/// Initizalizes data after enabling customer-level collection letters.
/// </summary>
private void enableCustomerLevelCollectionLetter()
{
    CustTable custTable;
    CustCollectionLetterJour custCollectionLetterJour;

    while select forupdate custTable
        exists join custCollectionLetterJour
            where custCollectionLetterJour.AccountNum == custTable.AccountNum
    {
        custTable.CollectionLetterCode = CustCollectionLetterJour::lastUpdatedCollectionLetterPerCustomer(custTable.AccountNum).CollectionLetterCode;
        custTable.doUpdate();
    }
}

/// <summary>
/// Checks if customer-level collection letter is enabled.
/// </summary>
/// <returns>
/// true if customer-level collection letter is enabled; otherwise, false.
/// </returns>
public boolean isCustomerLevelCollectionLetterEnabled()
{
    return this.CollectionLetterGenerationLevel == CustCollectionLetterGenerationLevel::Customer;
}

/// <summary>
/// Returns a reference to the business accounting debt model.
/// </summary>
/// <param name = "_throwOnError">
/// Specifies if an exception should be thrown on error.
/// </param>
/// <returns>
/// A reference to the business accounting debt model.
/// </returns>
public static RefRecId rTax25DebtModel(boolean _throwOnError = true)
{
    RefRecId ret = CustParameters::find().RTax25DebtModel;

    if (! ret && _throwOnError)
    {
        throw error("@RTax25BadDebtReserve:ModelNumberRAPSetMsg");
    }

    return ret;
}

