/// <summary>
/// Determines whether parameter "Base amount for sales tax" for collection write off is enabled.
/// </summary>
/// <param name = "_custParameters">
/// Cust parameters.
/// </param>
/// <returns>
/// true if parameter is enabled; otherwise, false.
/// </returns>
internal static boolean isWriteOffBaseAmountSalesTaxEnabled(CustParameters _custParameters = null)
{
    boolean isEnabled;
    CustParameters custParameters = (_custParameters) ? _custParameters : CustParameters::find();
    DefaultNoYes writeOffBaseAmountSalesTax = custParameters.CollectionsWriteOffBaseAmountSalesTax;
    if (TaxTable::isCountryWithTaxType_W())
    {
        isEnabled = (writeOffBaseAmountSalesTax != DefaultNoYes::No) ? true : false;
    }
    else
    {
        isEnabled = (writeOffBaseAmountSalesTax == DefaultNoYes::Yes) ? true : false;
    }
    return isEnabled;
}

private static boolean regionRequiresSequentialLineNumbersWhenPostingCustomerInvoiceLines()
{
    #ISOCountryRegionCodes
    return SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIT]);
}

internal static boolean canUseNonSequentialLineNumbersWhenPostingCustomerInvoiceLines()
{
    CustParameters custParameters = CustParameters::find();

    if (custParameters.UseSequentialLineNumbersWhenPostingCustomerInvoiceLines == NoYes::No
        && (CustParameters::regionRequiresSequentialLineNumbersWhenPostingCustomerInvoiceLines()
        || !CustInvoiceTransRandLineCreationSeqNumFlight::instance().isEnabled()))
    {
        custParameters.selectForUpdate(true);
        custParameters.UseSequentialLineNumbersWhenPostingCustomerInvoiceLines = NoYes::Yes;
        ttsbegin;
        custParameters.update();
        ttscommit;
    }

    return custParameters.UseSequentialLineNumbersWhenPostingCustomerInvoiceLines == NoYes::No;
}

