Method.Source:
edit CustExportFormat actionClassName(boolean _set, CustExportFormat _custExportFormat)
{
    CustPaymFormat custPaymFormat;
    // <GBR>
    ClassId classIdCur = this.ClassId;
    // </GBR>

    if (_set)
    {
        custPaymFormat = CustPaymFormat::findFormat(_custExportFormat, BankFormat::Export);
        if (custPaymFormat)
        {
            this.ClassId = custPaymFormat.ClassId;
        }
        else
        {
            this.ClassId = 0;
        }
    }
    else
    {
        custPaymFormat = CustPaymFormat::find(this.ClassId, BankFormat::Export);
        if (this.ClassId && !custPaymFormat)
        {
            this.ClassId = 0;
        }
    }
    // <GBR>
    if (BrazilParameters::isEnabled()
        && this.ClassId == classNum(CustOutPaym_CNAB_BR)
        && this.PaymSumBy != PaymSumBy::Invoice)
    {
        error(strFmt("@GLS1167", "@SYS14598", "@SYS12128", CustPaymFormat::find(this.ClassId, BankFormat::Export).Format));
        this.ClassId = classIdCur;
    }
    // </GBR>
    return custPaymFormat.Format;
}

