/// <summary>
/// Validates Period selected
/// </summary>
/// <returns>
/// Returns false with error message if Period is other than Invoice else returns true.
/// </returns>
public boolean checkPeriod_BR()
{
    if (BrazilParameters::isEnabled()
        && this.ClassId == classNum(CustOutPaym_CNAB_BR)
        && this.PaymSumBy != PaymSumBy::Invoice)
    {
        return checkFailed(
            strFmt("@GLS1167", "@SYS14598", "@SYS12128", CustPaymFormat::find(this.ClassId, BankFormat::Export).Format));
    }

    return true;
}

public void modifiedField(FieldId _fieldId)
{
    super(_fieldId);

    switch (_fieldId)
    {
        case fieldNum(CustPaymModeTable, AccountType):
            this.parmPaymentLedgerDimension(0);
            if (this.AccountType != LedgerJournalACType::Bank)
            {
                this.BankTransType = '';
            }
            break;

        case fieldNum(CustPaymModeTable, FurtherPosting):
            if (!this.FurtherPosting)
            {
                this.InterCompanyLedgerDimension = 0;
                this.PDCClearingPosting = NoYes::No;
                this.BridgingAccountByBank = NoYes::No;
            }
            break;

        case fieldNum(CustPaymModeTable, PaymOnInvoice):
            if (this.PaymOnInvoice == NoYes::No)
            {
                this.ExportOnInvoice = NoYes::No;
            }
            break;

        case fieldNum(CustPaymModeTable, PDCClearingPosting):
            if (this.PDCClearingPosting == NoYes::Yes)
            {
                this.FurtherPosting = NoYes::Yes;
                if (!FeatureStateProvider::isFeatureEnabled(BankBridgedTransAutoClearingFeature::instance()) || this.BridgingAccountByBank == NoYes::No)
                {
                    this.InterCompanyLedgerDimension = BankParameters::find().ReceiptClearingPDC;
                }
            }
            break;

        case fieldNum(CustPaymModeTable, BridgingAccountByBank):
            if (this.BridgingAccountByBank == NoYes::Yes)
            {
                this.FurtherPosting = NoYes::Yes;
                this.InterCompanyLedgerDimension = 0;
                this.AccountType = LedgerJournalACType::Bank;
            }
            break;

        // <GBR>
        case fieldNum(CustPaymModeTable, PrimaryMethod_BR):
            if (this.PrimaryMethod_BR != PrimaryMethod_BR::Other)
            {
                this.DescPrimaryMethod_BR = '';
            }
            break;
        // </GBR>

        case fieldNum(CustPaymModeTable, PaymentType):
            if (this.PaymentType != CustPaymentType::ElectronicPayment)
            {
                this.DirectDebit = NoYes::No;
            }
            break;
    }
}

// This method can return a field of type LedgerDimensionDefaultAccount or DimensionDynamicsDefaultAccount.
// We are using LedgerDimensionDefaultAccount as the return type as it is what DimensionDynamicsDefaultAccount extends from.
public LedgerDimensionDefaultAccount offsetLedgerDimension()
{
    return this.CustVendPaymModeTable::offsetLedgerDimension();
}

public CompanyBankAccountId parmPaymAccount(CompanyBankAccountId _account = '', LedgerJournalACType _accountType = this.AccountType, SelectableDataArea _currentCompany = curext())
{
    if (!prmisDefault(_accountType))
    {
        this.AccountType = _accountType;
    }

    // If company has not been specified, use the current company
    if (!_currentCompany || !xDataArea::exist(_currentCompany))
    {
        _currentCompany = curext();
    }

    LedgerJournalAC account;
    changecompany(_currentCompany)
    {
        // If account has been changed, update the backing ledger dimension value
        if (!prmisDefault(_account))
        {
            if (_account)
            {
                this.PaymentLedgerDimension = LedgerDynamicAccountHelper::getDynamicAccountFromAccountNumber(_account, _accountType);
                account = _account;
            }
            else
            {
                account = '';
                this.PaymentLedgerDimension = 0;
            }
        }
        else if (this.PaymentLedgerDimension)
        {
            // If the account has not been changed, then pull the value from ledger dimension as the master
            DimensionAttributeValueCombination ledgerDimension;
            select DisplayValue from ledgerDimension
                where ledgerDimension.RecId == this.PaymentLedgerDimension;
            account = ledgerDimension.DisplayValue;
        }
    }

    return account;
}

public DimensionDynamicDefaultAccount parmPaymentLedgerDimension(LedgerDimensionAccount _ledgerDimension = this.PaymentLedgerDimension)
{
    this.PaymentLedgerDimension = _ledgerDimension;

    return this.PaymentLedgerDimension;
}

