private boolean postValidateField_BR(FieldId _fieldId)
{
    boolean ret = true;

    switch (_fieldId)
    {
        case fieldNum(CustPaymModeTable, PaymSumBy),
            fieldNum(CustPaymModeTable, ReturnLayoutGroupId_BR),
            fieldNum(CustPaymModeTable, ClassId),
            fieldNum(CustPaymModeTable, ExportLayoutGroupId_BR):
            ret = this.checkPeriod_BR();
            break;

        case fieldNum(CustPaymModeTable, PaymentMethod_BR):
            if (this.PaymentMethod_BR == EFDocPaymentMethod_BR::TradeCard)
            {
                ret = checkFailed(strFmt("@NFE:InvalidValueForNFE4", this.PaymentMethod_BR));
            }
            break;
    }

    return ret;
}

/// <summary>
/// Validates record post validateWrite method
/// </summary>
/// <returns>
/// Returns false with error message if validation fails else returns true.
/// </returns>
public boolean postValidateWrite_BR()
{
    boolean ret = this.validateExportFormat_BR();
    ret = this.validateReturnFormat_BR() && ret;
    ret = this.validateDescPrimaryMethod_BR() && ret;
    ret = this.validateLayoutGroups_BR() && ret;
    return ret;
}

private boolean validateDescPrimaryMethod_BR()
{
    if (this.PrimaryMethod_BR == PrimaryMethod_BR::Other && !this.DescPrimaryMethod_BR)
    {
        return checkFailed(
            strFmt("@GLS50957", fieldPName(CustPaymModeTable, PrimaryMethod_BR), enum2Value(PrimaryMethod_BR::Other), fieldPName(CustPaymModeTable, DescPrimaryMethod_BR)));
    }

    return true;
}

/// <summary>
///     Checks for the export format based on payment type.
/// </summary>
/// <returns>
/// true if payment type is electronic payment and export format is configurable layout file; otherwise, false.
/// </returns>
private boolean validateExportFormat_BR()
{
    if (this.UseGERConfiguration == NoYes::No)
    {
        if (this.ClassId == classNum(CustOutPaym_CNAB_BR) && this.checkCustPaymentTypeElectronicPayment())
        {
            return true;
        }
        else if (this.ClassId == classNum(CustOutPaym_CNAB_BR) || this.checkCustPaymentTypeElectronicPayment())
        {
            return checkFailed(strFmt("@SYS4000747", enum2str(CustPaymentType::ElectronicPayment), new CustOutPaym_CNAB_BR().interfaceName()));
        }
    }

    return true;
}

/// <summary>
///     Checks if field <c>PaymentType</c> has value electronic payment of enum <c>CustPaymentType</c>.
/// </summary>
/// <returns>
///     true if <c>PaymentType</c> is of type electronic payment; otherwise, false.
/// </returns>
protected boolean checkCustPaymentTypeElectronicPayment()
{
    return this.PaymentType == CustPaymentType::ElectronicPayment;
}

public boolean validateField(FieldId _fieldIdToCheck)
{
    boolean isValid = super(_fieldIdToCheck);

    if (isValid)
    {
        switch (_fieldIdToCheck)
        {
            case fieldNum(CustPaymModeTable, PaymentLedgerDimension):
                if (this.AccountType == LedgerJournalACType::Bank && this.parmPaymAccount())
                {
                  isValid = BankAccountTable::checkIsActive(this.parmPaymAccount());
                }
                break;
        }
    }

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        isValid = this.postValidateField_BR(_fieldIdToCheck) && isValid;
    }
    // </GBR>

    return isValid;
}

/// <summary>
///     Checks for existence of export layout group and return layout group.
/// </summary>
/// <returns>
///     true if export layout group and return layout group exists; otherwise, false.
/// </returns>
private boolean validateLayoutGroups_BR()
{
    boolean ret = true;

    if (this.checkCustPaymentTypeElectronicPayment() && this.UseGERConfiguration == NoYes::No)
    {
        if (!this.ExportLayoutGroupId_BR)
        {
            ret = checkFailed(strFmt("@SYS26332", fieldPName(CustPaymModeTable, ExportLayoutGroupId_BR)));
        }
        if (!this.ReturnLayoutGroupId_BR)
        {
            ret = checkFailed(strFmt("@SYS26332", fieldPName(CustPaymModeTable, ReturnLayoutGroupId_BR)));
        }
    }

    return ret;
}

/// <summary>
///     Checks for the return format based on payment type.
/// </summary>
/// <returns>
/// true if payment type is electronic payment and return format is configurable layout file; otherwise, false.
/// </returns>
private boolean validateReturnFormat_BR()
{
    if (this.UseGERConfiguration == NoYes::No)
    {
        if (this.ClassIdReturn == classNum(CustPaymReconcilImport_EP_Return_BR) && this.checkCustPaymentTypeElectronicPayment())
        {
            return true;
        }
        else if (this.ClassIdReturn == classNum(CustPaymReconcilImport_EP_Return_BR) || this.checkCustPaymentTypeElectronicPayment())
        {
            return checkFailed(strFmt("@SYS4000810", enum2str(CustPaymentType::ElectronicPayment), new CustPaymReconcilImport_EP_Return_BR().interfaceName()));
        }
    }

    return true;
}

public boolean validateWrite()
{
    boolean ret = super();
    this.checkIfERFormatsChosen();

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        ret = this.postValidateWrite_BR() && ret;
    }
    // </GBR>

    return ret;
}

public void write()
{
    if (this.ClassId != this.orig().ClassId)
    {
        CustPaymModeSpec custPaymModeSpec;
        delete_from custPaymModeSpec
            where custPaymModeSpec.PaymMode == this.PaymMode;
    }
    super();
}

