{
    return this.ExportRefund ?
        CustVendOutPaymConstants::creditTransferContainerDescriptorName :
        CustVendOutPaymConstants::derictDebitContainerDescriptorName;
}

public static boolean checkExist(PaymMode _paymMode)
{
    if (!CustPaymModeTable::exist(_paymMode))
    {
        return checkFailed(strFmt(CustPaymModeTable::txtNotExist(), _paymMode));
    }

    return true;
}

public static DiscGraceDays discGraceDays(PaymMode _paymMode)
{
    CustPaymModeTable custPaymModeTable;

    if (_paymMode)
    {
        select firstonly DiscGraceDays from custPaymModeTable
            where custPaymModeTable.PaymMode == _paymMode;
    }
    return custPaymModeTable.DiscGraceDays;
}

public static boolean exist(PaymMode _paymMode)
{
    return (select firstonly RecId from custPaymModeTable
                where custPaymModeTable.PaymMode == _paymMode).RecId != 0;
}

public static CustPaymModeTable find(
    PaymMode _paymMode,
    boolean _forupdate = false,
    ConcurrencyModel _concurrencyModel = ConcurrencyModel::Auto)
{
    CustPaymModeTable custPaymModeTable;

    custPaymModeTable.selectForUpdate(_forupdate);
    if (_forupdate && _concurrencyModel != ConcurrencyModel::Auto)
    {
        custPaymModeTable.concurrencyModel(_concurrencyModel);
    }

    select firstonly custPaymModeTable
        where custPaymModeTable.PaymMode == _paymMode;

    return custPaymModeTable;
}

