/// <summary>
/// Check if it is by Cash(Method Of Payment).
/// </summary>
/// <param name="_paymMode">
/// PaymMode.
/// </param>
/// <returns>
/// true is line is by cash, otherwise false.
/// </returns>
public static boolean isbyCashMethodOfPayment_IN(PaymMode _paymMode)
{
    CustPaymModeTable paymModeTableLoc;

    select firstonly RecId from paymModeTableLoc
        where paymModeTableLoc.PaymMode == _paymMode
            && (paymModeTableLoc.AccountType == LedgerJournalACType::Bank
                || paymModeTableLoc.AccountType == LedgerJournalACType::Ledger)
            && paymModeTableLoc.PaymOnInvoice
            && paymModeTableLoc.PaymJournalNameId != '';

    return paymModeTableLoc.RecId;
}

public static void lookupPaymMethod(FormStringControl _stringControl, str _filterStr, CompanyId _company)
{
    CompanyId company = _company;

    if (company == '')
    {
        company = curext();
    }

    changecompany(company)
    {
        Args args = new Args();
        args.name(formStr(CustPaymModeTableLookup));
        args.lookupField(fieldNum(CustPaymModeTable, PaymMode));
        args.lookupValue(_filterStr);
        args.caller(_stringControl);

        FormRun formRun = classfactory.formRunClass(args);
        formRun.init();

        _stringControl.performFormLookup(formRun);
    }
}

/// <summary>
/// Looks up method to find payment methods that are 'OnAccount' or 'Check'.
/// </summary>
/// <param name="_ctrl">
/// Passed in control for which the look up is needed.
/// </param>
public static void mcrLookupSubSetMethods(FormStringControl _ctrl)
{
    Query query = new Query();
    query
        .addDataSource(tableNum(CustPaymModeTable))
        .addRange(fieldNum(CustPaymModeTable, DataAreaId))
        .value(
            strFmt('((%1 == %2) || (%1 == %3))', fieldStr(CustPaymModeTable, PaymentType), enum2int(CustPaymentType::Other), enum2int(CustPaymentType::Check)));

    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(CustPaymModeTable), _ctrl);
    sysTableLookup.addLookupfield(fieldNum(CustPaymModeTable, PaymMode));
    sysTableLookup.addLookupfield(fieldNum(CustPaymModeTable, Name));
    sysTableLookup.addLookupfield(fieldNum(CustPaymModeTable, PaymentType));
    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

/// <summary>
///    Creates a default dimension based on the specified values.
/// </summary>
/// <param name="_paymMode">
///    A record in the <c>PaymMode</c> table from which to use the default settings.
/// </param>
/// <param name="_defaultDimension">
///    The record ID of a record in the <c>DimensionAttributeValueSetItem</c> table that represents the
///    customer transaction default dimension.
/// </param>
/// <param name="_curDefaultDimension">
///    The record ID of a record in the <c>DimensionAttributeValueSetItem</c> table that represents the
///    current default dimension.
/// </param>
/// <returns>
///    The record ID of a record in the <c>DimensionAttributeValueSetItem</c> table that represents the
///    calculated default dimension.
/// </returns>
public static DimensionDefault setDefaultDimension(PaymMode _paymMode, DimensionDefault _defaultDimension, DimensionDefault _curDefaultDimension)
{
    DimensionDefault defaultDimension;
    if (CustVendPaymProposalKeepDefaultDimensionNOFlight::instance().isEnabled())
    {
        defaultDimension = _defaultDimension ? _defaultDimension : _curDefaultDimension;
    }
    else
    {
        defaultDimension = _defaultDimension;
    }

    CustPaymModeTable custPaymModeTable = CustPaymModeTable::find(_paymMode);

    if (custPaymModeTable.DimCtrl == NoYes::Yes)
    {
        DimensionAttributeValueSetStorage valueSetStorage = new DimensionAttributeValueSetStorage();

        if (!CustVendPaymProposalKeepDefaultDimensionNOFlight::instance().isEnabled())
        {
            defaultDimension = _defaultDimension ? _defaultDimension : _curDefaultDimension; // custTrans.dimension has first priority
        }

        DimensionAttributeSetItem dimUse;
        DimensionAttributeValueSetItemView setItemView;
        DimensionAttributeValue dimAttrValue;

        // Evaluate each value in the incoming default dimension and add to the set if specified
        while select DimensionAttribute, DimensionAttributeValue from setItemView
            where setItemView.DimensionAttributeValueSet == defaultDimension
            join RecId from dimUse
                where dimUse.DimensionAttribute == setItemView.DimensionAttribute
                    && dimUse.DimensionAttributeSet == custPaymModeTable.DimensionAttributeSet
                    && dimUse.EnumerationValue == NoYes::Yes
            join HashKey, EntityInstance from dimAttrValue
                where dimAttrValue.RecId == setItemView.DimensionAttributeValue
        {
            valueSetStorage.addItemValues(setItemView.DimensionAttribute, setItemView.DimensionAttributeValue, dimAttrValue.HashKey, dimAttrValue.EntityInstance);
        }

        defaultDimension = valueSetStorage.save();
    }
    else if (!CustVendPaymProposalKeepDefaultDimensionNOFlight::instance().isEnabled()
        && SysCountryRegionCode::isLegalEntityInCountryRegion([#isoNO])
        && SkipLedgerDimensionInPaymentProposalNOFlight::instance().isEnabled())
    {
        defaultDimension = 0;
    }
    return defaultDimension;
}

private static TxtNotExist txtNotExist()
{
    return "@SYS16654";
}

/// <summary>
/// Get company bank account table.
/// </summary>
