public BankAccountTable bankAccountTable()
{
    return BankAccountTable::find(this.parmPaymAccount());
}

private void checkIfERFormatsChosen()
{
    if (this.UseGERConfiguration == NoYes::Yes && !this.ERFormatMappingID)
    {
        warning(strFmt("@AccountsReceivable:ExportFormatConfigurationNotSet", this.PaymMode));
    }

    if (this.UseGERImport == NoYes::Yes && !this.ERModelMappingTable)
    {
        warning(strFmt("@AccountsReceivable:ImportFormatConfigurationNotSet", this.PaymMode));
    }
}

/// <summary>
/// Gets the method of payment for the given payment type.
/// </summary>
/// <param name = "_paymentType">The payment type.</param>
/// <returns>The method of payment corresponding to the payment type.</returns>
internal static CustPaymMode getPaymMode(CustPaymentType _paymentType)
{
    CustPaymModeTable custPaymModeTableLocal;

    select firstonly PaymMode from custPaymModeTableLocal
        where custPaymModeTableLocal.PaymentType == _paymentType;

    return custPaymModeTableLocal.PaymMode;
}
