void  initFromSalesQuotationTable(SalesQuotationTable  _salesQuotationTable)
{
    this.QuotationType          = _salesQuotationTable.QuotationType;
    this.BusRelAccount          = _salesQuotationTable.BusRelAccount;
    this.OrderAccount           = _salesQuotationTable.CustAccount;
    this.InvoiceAccount         = _salesQuotationTable.InvoiceAccount;

    this.ProjIdRef              = _salesQuotationTable.ProjIdRef;
    this.ProjInvoiceProjId      = _salesQuotationTable.ProjInvoiceProjId;
    this.ModelId                = _salesQuotationTable.ModelId;
    this.CampaignId             = _salesQuotationTable.CampaignId;

    this.DeliveryName           = _salesQuotationTable.DeliveryName ;
    this.DeliveryPostalAddress  = _salesQuotationTable.DeliveryPostalAddress;

    this.PurchaseOrder          = _salesQuotationTable.CustPurchaseOrder;
    this.CustomerRef            = _salesQuotationTable.CustomerRef;
    this.DlvTerm                = _salesQuotationTable.DlvTerm;
    this.DlvMode                = _salesQuotationTable.DlvMode;

    if (_salesQuotationTable.BusRelAccount)
    {
        this.CustGroup          = smmBusRelTable::find(_salesQuotationTable.BusRelAccount).CustGroup;
    }
    else
    {
        this.CustGroup          = CustTable::find(_salesQuotationTable.CustAccount).CustGroup;
    }

    this.WorkerSalesTaker       = _salesQuotationTable.WorkerSalesTaker;
    this.LanguageId             = _salesQuotationTable.LanguageId;
    this.Payment                = _salesQuotationTable.Payment;
    this.FixedDueDate           = _salesQuotationTable.FixedDueDate;
    this.CashDiscCode           = _salesQuotationTable.CashDisc;
    this.InclTax                = _salesQuotationTable.InclTax;

    this.Qty                    = 0;
    this.Volume                 = 0;
    this.Weight                 = 0;
    this.CostValue              = 0;
    this.SumLineDisc            = 0;
    this.SumMarkup              = 0;
    this.SalesBalance           = 0;
    this.EndDisc                = 0;
    this.RoundOff               = 0;
    this.QuotationAmount        = 0;
    this.CurrencyCode           = _salesQuotationTable.CurrencyCode;

    this.Triangulation          = _salesQuotationTable.euroTriangulation(this.QuotationDate);
    this.ExchRate               = _salesQuotationTable.exchRate(this.QuotationDate);
    this.ExchRateSecondary      = _salesQuotationTable.exchRateSecondary(this.QuotationDate);

    this.DefaultDimension       = this.copyDimension(_salesQuotationTable.DefaultDimension);
}

