public boolean isSummaryUpdated()
{
    Query                           query = this.queryCustQuotationConfirmSalesLink();
    QueryRun                        queryRun;
    CustQuotationConfirmSalesLink   salesLink;

    query.dataSourceTable(tableNum(CustQuotationConfirmSalesLink)).firstOnly(true);
    query.dataSourceTable(tableNum(CustQuotationConfirmSalesLink)).addRange(fieldNum(CustQuotationConfirmSalesLink, OrigSalesId)).value(strFmt('!%1',queryValue(this.SalesId)));

    queryRun = new QueryRun(query);

    if (queryRun.next())
    {
        salesLink = queryRun.get(tableNum(CustQuotationConfirmSalesLink));
        return (salesLink.OrigSalesId != '');
    }

    return false;
}

