public void  printJournal(
    SalesQuotationEditLinesForm _salesQuotationEditLines = null,
    RecordSortedList            _journalList     = null)
{
    Args parameters = this.initializeParametersForJournalPrint(_salesQuotationEditLines, _journalList);

    this.printJournalMenu().run(parameters);
}

/// <summary>
/// Determines if the confirmation journal is for a project based quotation.
/// </summary>
/// <returns>true if the confirmation journal is for a project based quotation; otherwise, false.</returns>
protected boolean isProjectBasedQuotation()
{
    return isConfigurationkeyEnabled(configurationKeyNum(Project))
        && this.QuotationType == QuotationType::Project;
}

private Args initializeParametersForJournalPrint(
    SalesQuotationEditLinesForm _salesQuotationEditLines,
    RecordSortedList            _journalList)
{
    Args parameters = new Args();

    if (this.isProjectBasedQuotation())
    {
        SysDictEnum     enum = new SysDictEnum(enumNum(Assessment));
        Assessment      assessment =  _salesQuotationEditLines.parmPSAAssessment();
        parameters.parm(enum.value2Symbol(assessment));
    }

    parameters.caller(_salesQuotationEditLines);
    if (_journalList)
    {
        parameters.object(_journalList);
    }
    else
    {
        parameters.record(this);
    }

    return parameters;
}

/// <summary>
/// The menu for the confirmation journal.
/// </summary>
/// <returns>A <c>MenuFunction</c> for the confirmation journal.</returns>
[Replaceable]
protected MenuFunction printJournalMenu()
{
    if (this.isProjectBasedQuotation())
    {
        return new MenuFunction(menuitemOutputStr(PSAQuotationsConfirmPrint), MenuItemType::Output);
    }

    return new MenuFunction(menuitemOutputStr(SalesQuotationConfirmation), MenuItemType::Output);
}

Query queryCustQuotationConfirmSalesLink()
{
    return SysQuery::queryFromTableRelation(this,tableNum(CustQuotationConfirmSalesLink));
}

Query queryTaxJournalTrans(boolean update = false)
{
    DictRelation    dictRelation;
    DictField       externdictField;
    DictField       dictField;
    Query           query = new Query();
    Counter         c;

    query.addDataSource(tableNum(TaxJournalTrans));

    dictRelation = new DictRelation(tableNum(TaxJournalTrans));
    dictRelation.loadTableRelation(this.TableId);

    if (dictRelation.lines())
    {
        for (c=1; c <= dictRelation.lines(); c++)
        {
            externdictField   = new DictField(this.TableId,dictRelation.lineExternTableValue(c));
            dictField   = new DictField(tableNum(TaxJournalTrans),dictRelation.lineTableValue(c));
            query.dataSourceTable(tableNum(TaxJournalTrans)).addRange(dictField.id()).value(queryValue(this.(externdictField.id())));
        }
    }
    query.dataSourceTable(tableNum(TaxJournalTrans)).update(update);
    query.dataSourceTable(tableNum(TaxJournalTrans)).addRange(fieldNum(TaxJournalTrans, TaxOrigin)).value(strFmt('!%1',queryValue(TaxOrigin::TaxReversed)));

    return query;
}

Query queryTmpTaxWorkTrans(boolean update = false)
{
    Query           query = new Query();

    query.addDataSource(tableNum(TmpTaxWorkTrans));

    query.dataSourceTable(tableNum(TmpTaxWorkTrans)).update(update);
    query.dataSourceTable(tableNum(TmpTaxWorkTrans)).addRange(fieldNum(TmpTaxWorkTrans, TaxOrigin)).value(strFmt('!%1',queryValue(TaxOrigin::TaxReversed)));

    return query;
}

