SalesQuotationTable salesQuotationTable()
{
    CustQuotationConfirmSalesLink    custQuotationConfirmSalesLink;

    select firstonly OrigQuotationId from custQuotationConfirmSalesLink
        order by QuotationDate desc, QuotationConfirmId desc
        where custQuotationConfirmSalesLink.QuotationConfirmId  == this.QuotationConfirmId
           && custQuotationConfirmSalesLink.QuotationDate       == this.QuotationDate;

    return SalesQuotationTable::find(custQuotationConfirmSalesLink.OrigQuotationId);
}

SalesTable salesTable(boolean update = false)
{
    return SalesTable::find(this.SalesId, update);
}

void setDocNum(Common      _common)
{
    SalesTable              salesTable;
    SalesQuotationTable     salesQuotationTable;
    SalesQuotationLine      salesQuotationLine;
    CustQuotationTrans      custQuotationTrans;
    CustQuotationSalesLink  custQuotationSalesLink;

    if (_common.RecId)
    {
        switch (_common.TableId)
        {
            case tableNum(SalesTable):
                salesTable = _common;
                this.QuotationDocNum = strFmt('%1-%2', this.SalesId, CustQuotationConfirmJour::numberOfQuotations(salesTable) + 1);
                break;

            case tableNum(SalesQuotationTable):
                salesQuotationTable = _common;

                select firstonly custQuotationSalesLink
                    order by QuotationDate desc,QuotationId desc
                    where custQuotationSalesLink.OrigQuotationId == salesQuotationTable.QuotationId;

                if (custQuotationSalesLink)
                {
                    this.QuotationDocNum = CustQuotationJour::findFromSalesQuotationQuotation(custQuotationSalesLink.QuotationId).QuotationDocNum;
                }
                else
                {
                    this.QuotationDocNum = strFmt('%1-%2', salesQuotationTable.QuotationId, CustQuotationConfirmJour::numberOfQuotations(salesQuotationTable) + 1);
                }
                break;

            case tableNum(SalesQuotationLine):
                salesQuotationLine = _common;

                select firstonly QuotationId from custQuotationSalesLink
                    join TableId from custQuotationTrans
                        order by QuotationDate desc,QuotationId desc
                        where custQuotationSalesLink.QuotationId == custQuotationTrans.QuotationId
                           && custQuotationTrans.InventTransId   == salesQuotationLine.InventTransId;

                if (custQuotationSalesLink)
                {
                    this.QuotationDocNum = CustQuotationJour::findFromSalesQuotationQuotation(custQuotationSalesLink.QuotationId).QuotationDocNum;
                }
                else
                {
                    this.QuotationDocNum = strFmt('%1-%2', salesQuotationLine.QuotationId, CustQuotationConfirmJour::numberOfQuotations(salesQuotationLine) + 1);
                }
                break;
        }
    }
}

display SalesQuotationStatus showQuotationStatus()
{
    SalesQuotationStatus    retStatus;

    switch (this.salesQuotationTable().QuotationStatus)
    {
        case SalesQuotationStatus::Sent                 :
            retStatus = SalesQuotationStatus::Sent;
            break;

        case SalesQuotationStatus::Confirmed            :
            retStatus = SalesQuotationStatus::Confirmed;
            break;
        default                                         :
            retStatus = SalesQuotationStatus::Sent;
    }

    return retStatus;
}

