static boolean  exist(SalesId  salesId)
{
    return salesId && (select firstonly RecId from custQuotationConfirmJour
                index hint SalesIdx
                where custQuotationConfirmJour.SalesId == salesId
           ).RecId != 0;
}

static boolean exist_BusRelAccount(smmBusRelAccount _busRelAccount)
{
    return (select RecId from custQuotationConfirmJour where custQuotationConfirmJour.BusRelAccount == _busRelAccount).RecId != 0;
}

static boolean exist_custAccount(CustAccount _custAccount)
{
    return (select RecId from custQuotationConfirmJour where custQuotationConfirmJour.OrderAccount == _custAccount).RecId != 0;
}

static CustQuotationConfirmJour  findFromCustQuotationConfirmTrans(SalesId  salesId,
                                                     QuotationConfirmId     quotationId,
                                                     TransDate              quotationDate,
                                                     boolean                update = false)
{
    CustQuotationConfirmJour  custQuotationConfirmJour;

    if (quotationId)
    {
        custQuotationConfirmJour.selectForUpdate(update);

        select firstonly custQuotationConfirmJour
            where custQuotationConfirmJour.SalesId                  == salesId
               && custQuotationConfirmJour.QuotationConfirmId       == quotationId
               && custQuotationConfirmJour.QuotationDate            == quotationDate;
    }

    return custQuotationConfirmJour;
}

static CustQuotationConfirmJour findFromSalesQuotationQuotation(QuotationIdBase    quotationId,
                                                         boolean            update = false)
{
    CustQuotationConfirmJour  custQuotationConfirmJour;

    if (quotationId)
    {
        custQuotationConfirmJour.selectForUpdate(update);

        select firstonly custQuotationConfirmJour
            where custQuotationConfirmJour.QuotationConfirmId     == quotationId;
    }

    return custQuotationConfirmJour;
}

static CustQuotationConfirmJour findRecId(recId    recId,
                                   boolean  update = false)
{
    CustQuotationConfirmJour custQuotationConfirmJour;

    custQuotationConfirmJour.selectForUpdate(update);

    select firstonly custQuotationConfirmJour
        index hint recid
        where custQuotationConfirmJour.RecId == recId;

    return custQuotationConfirmJour;
}

static recId numberOfQuotations(Common _common)
{
    SalesTable                      salesTable;
    SalesQuotationTable             salesQuotationTable;
    recId                           totalQuotations;
    CustQuotationConfirmJour        custQuotationConfirmJour;
    CustQuotationConfirmSalesLink          custQuotationConfirmSalesLink;

    if (_common.RecId)
    {
        switch (_common.TableId)
        {
            case tableNum(SalesTable)           :
                salesTable          = _common;
                totalQuotations = (select count (RecId) from custQuotationConfirmJour
                    index hint SalesIdx
                    where custQuotationConfirmJour.SalesId == salesTable.SalesId).RecId;

                break;

            case tableNum(SalesQuotationTable)  :
                salesQuotationTable = _common;
                totalQuotations = (select count (RecId) from custQuotationConfirmSalesLink
                    where custQuotationConfirmSalesLink.OrigQuotationId == salesQuotationTable.QuotationId).RecId;
                break;
        }
    }

    return totalQuotations;
}
