        strFmt('(((%1.%2 != %6) && (%1.%2 >= %3) && (%1.%2 <= %4)) || ((%1.%2 == %6) && (%1.%5 >= %3) && (%1.%5 <= %4)))',
            custTransQueryBuildDataSource.name(),
