/// </returns>
display CNPJCPFNum_BR subscriptionType_BR()
{
    if (this.partyType() == DirPartyType::Organization)
    {
        return '02';
    }
    else if (this.partyType() == DirPartyType::Person)
    {
        return '01';
    }
    else
    {
        return '';
    }
}

