public AmountMST amountMST(AmountCur _amountCur)
{
    return CurrencyExchangeHelper::mstAmount(_amountCur, this.CurrencyCode, this.InvoiceDate, Currency::noYes2UnknownNoYes(this.euroTriangulation()), this.exchRate(), this.exchRateSecondary());
}

public void calcDue()
{
    if (! this.DueDate)
    {
        this.DueDate = this.paymentDueDate();
    }
}

public void calcInvoiceAmount()
{
    CzVendAdvanceInvoiceLine invoiceLine;

    select sum(AmountCur)
    from invoiceLine
        where invoiceLine.ParentRecId == this.RecId;

    this.InvoiceAmount = invoiceLine.AmountCur;
}

private void checkCurrencyCode()
{
    if (this &&
        this.CurrencyCode != this.orig().CurrencyCode &&
        MarkupTrans::existDifferentCurrency(this.TableId,
                                            this.RecId,
                                            this.CurrencyCode))
    {
        warning("@SYS24528");

        this.CurrencyCode = this.orig().CurrencyCode;
    }
}

display ContactPersonName contactPersonName()
{
    return ContactPerson::name(this.ContactPersonId);
}

public void convertCurrencyCode(CurrencyCode _currency)
{
    CzVendAdvanceInvoiceLine invoiceLine;
    MarkupTrans              markupTrans;

    ttsbegin;

    while select forupdate invoiceLine
        where invoiceLine.ParentRecId == this.RecId
    {
        invoiceLine.convertCurrencyCode(_currency, this.InvoiceDate);
        invoiceLine.doUpdate();
    }

    while select forupdate markupTrans
        where markupTrans.TransTableId == this.TableId &&
              markupTrans.TransRecId   == this.RecId
    {
        markupTrans.convertCurrencyCode(_currency,  this.InvoiceDate);
        markupTrans.doUpdate();
    }

    CustPaymSched::deleteCustPaymSched(this.TableId, this.RecId);

    this.calcInvoiceAmount();
    this.CurrencyCode = _currency;

    ttscommit;
}

private void createOneTimeAccount()
{
    this.OrderAccount = VendTable::createOneTimeAccount(this);

    if (! this.InvoiceAccount)
    {
        this.InvoiceAccount = this.OrderAccount;
    }
}

public void delete()
{
    ttsbegin;

    SpecTransManager::newFromSpec(this).deleteAll();

    super();

    ttscommit;
}

