public void initFromPayment()
{
    this.PaymentSched = this.payment().PaymSched;
}

private void initFromVendTable(VendTable _vendTable)
{
    this.Payment              = _vendTable.PaymTermId;
    this.initFromPayment();

    if (_vendTable.PaymSched)
    {
        this.PaymentSched     = _vendTable.PaymSched;
    }

    this.PaymMode             = _vendTable.PaymMode;
    this.PaymSpec             = _vendTable.PaymSpec;

    if (!this.InvoiceDate)
        this.InvoiceDate      = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone());

    this.calcDue();

    this.LanguageId           = DirPartyTable::findRec(_vendTable.Party).LanguageId;
    this.VendGroup            = _vendTable.VendGroup;

    this.OrderAccount         = _vendTable.AccountNum;
    this.InvoiceAccount       = this.InvoiceAccount ?
                                this.InvoiceAccount :
                                (_vendTable.InvoiceAccount ?
                                 _vendTable.InvoiceAccount :
                                 _vendTable.AccountNum);

    this.NumberSequenceGroup  = _vendTable.NumberSequenceGroup;

    this.DefaultDimension     = _vendTable.DefaultDimension;

    this.PostingProfile       = VendParameters::find().PrepaymentPostingProfile;
    if (! this.PostingProfile)
    {
        this.PostingProfile   = VendParameters::find().PostingProfile;
    }

    this.CurrencyCode         = _vendTable.Currency;

    this.setInvoiceAddress();

    this.setListcode();
}

public void initRecord()
{
    VendTable   vendTable = this.vendTable_OrderAccount();
    VendTable   invoiceTable;

    this.initFromVendTable(vendTable);

    if (! this.RecId && this.InvoiceAccount !=  this.OrderAccount)
    {
        invoiceTable = VendTable::find(this.InvoiceAccount);

        if (Box::yesNo("@SYS24000"+ '\n\n' +
                       VendTable::promptAddress(this.OrderAccount)+ '\n\n' +
                       "@SYS15413" + '\n\n' +
                       invoiceTable.InvoiceAccount + '\n\n' +
                       "@SYS6182",
                       DialogButton::Yes,
                       "@SYS29051",
                       "@SYS29052") == DialogButton::No)
        {
            this.initFromVendTable(vendTable);
        }
        else
        {
            this.initFromVendTable(invoiceTable);
            this.OrderAccount = vendTable.AccountNum;
        }
    }

    this.calcDue();
}

public void insert()
{
    ttsbegin;

    if (this.OneTimeVendor && ! this.OrderAccount)
        this.createOneTimeAccount();

    if (! this.InvoiceAccount)
    {
        this.InvoiceAccount = this.OrderAccount;
    }

    this.Status = CzAdvanceInvoiceStatus::Open;

    super();

    ttscommit;
}

