/// <summary>
///     Determines whether an advance invoice exists for a specific purchase order.
/// </summary>
/// <param name="_purchId">
///     <c>PurchId</c> of the purchase order.
/// </param>
/// <returns>
/// true if an advance invoice exists; otherwise false.
/// </returns>
public static boolean existsPurchId(PurchId _purchId)
{
    return (select firstonly RecId from CzVendAdvanceInvoiceTable where
                CzVendAdvanceInvoiceTable.PurchId == _purchId).RecId ? true : false;
}

/// <summary>
/// Finds the advance invoices linked to the specified payment that contain different tax groups than the payment.
/// </summary>
/// <param name="_payment">
/// A payment that may be linked to one or more advance invoices.
/// </param>
/// <returns>
/// A dataset of advance invoices with different tax groups than the given payment, positioned on the first
/// record; otherwise, an empty record buffer.
/// </returns>
/// <remarks>
/// The use of the 'firstonly' keyword is intentionally avoided so callers can iterate the possible dataset.
/// </remarks>
public static CzVendAdvanceInvoiceTable findAllForPaymentDifferentTaxGroups(LedgerJournalTrans _payment)
{
    CzVendAdvanceInvoiceLink link;
    CzVendAdvanceInvoiceLine advanceInvoiceLine;
    CzVendAdvanceInvoiceTable advanceInvoiceTable;

    select crosscompany advanceInvoiceTable
        exists join advanceInvoiceLine
            where advanceInvoiceLine.ParentRecId == advanceInvoiceTable.RecId &&
                  (advanceInvoiceLine.TaxGroup     != _payment.TaxGroup ||
                   advanceInvoiceLine.TaxItemGroup != _payment.TaxItemGroup)
        exists join link
            where link.InvoiceCompany      == advanceInvoiceTable.DataAreaId &&
                  link.AdvanceInvoiceRecId == advanceInvoiceTable.RecId &&
                  link.PaymentCompany      == _payment.DataAreaId &&
                  link.Voucher             == _payment.Voucher &&
                  link.TransDate           == _payment.TransDate;

    return advanceInvoiceTable;
}

public static CzVendAdvanceInvoiceTable findByCompany(
    CompanyId   _companyId,
    recId       _recId ,
    boolean     _forupdate = false)
{
    CzVendAdvanceInvoiceTable advanceInvoiceTable;

    changecompany (_companyId)
    {
        advanceInvoiceTable = CzVendAdvanceInvoiceTable::findRecId(_recId, _forupdate);
    }

    return advanceInvoiceTable;
}

public static CzVendAdvanceInvoiceTable findInvoiceId(InvoiceId _invoiceId,
                                                             boolean   _update = false)
{
    CzVendAdvanceInvoiceTable table;

    table.selectForUpdate(_update);

    if (_invoiceId)
    {
        select firstonly table
            where table.InvoiceId == _invoiceId;
    }

    return table;
}

public static CzVendAdvanceInvoiceTable findRecId(RecId _recId, boolean _update = false)
{
    CzVendAdvanceInvoiceTable table;

    table.selectForUpdate(_update);

    select firstonly table
        where table.RecId == _recId;

    return table;
}

