public static CzVendAdvanceInvoiceTable findByHeadId(SysGuid _headId, boolean _forUpdate = false)
{
    CzVendAdvanceInvoiceTable invoiceTable;

    invoiceTable.selectForUpdate(_forUpdate);

    select firstonly invoiceTable
        where invoiceTable.HeadId == _headId;

    return invoiceTable;
}

