/// <summary>
/// Retrieves the invoice ID from the source document.
/// </summary>
/// <returns>
/// The invoice ID.
/// </returns>
public InvoiceId invoiceId()
{
    InvoiceId       invoiceId;

    switch (this.RefTableId)
    {
        case tableNum(CustInvoiceJour) :
            invoiceId = CustInvoiceJour::findRecId(this.RefRecId).InvoiceId;
            break;

        case tableNum(ProjInvoiceJour) :
            invoiceId = ProjInvoiceJour::findRecId(this.RefRecId).ProjInvoiceId;
            break;

        case tableNum(RetailStatementJour) :
            invoiceId = RetailStatementJour::findRecId(this.RefRecId).statementId;
            break;

        case tableNum(RetailEInvoiceAggregatedStatement_MX) :
            invoiceId = RetailEInvoiceAggregatedStatement_MX::findRecId(this.RefRecId).AggregatedStatementId;
            break;

        default:
            throw error(error::wrongUseOfFunction(funcName()));
    }

    return invoiceId;
}

