/// Gets the transaction datetime of the current <c>EInvoicejour_MX</c> record.
/// </summary>
/// <returns>
/// The transaction datetime of the current <c>EInvoicejour_MX</c> record.
/// </returns>
public utcdatetime documentDateTime()
{
    return DateTimeUtil::applyTimeZoneOffset(this.InvoiceDateTime, DateTimeUtil::getUserPreferredTimeZone());
}

/// <summary>
/// Indicates whether the specified record in the <c>EInvoiceJour_MX</c> table exists.
/// </summary>
/// <param name="_recId">
/// The record ID of the <c>EInvoiceJour_MX</c> record.
/// </param>
/// <returns>
/// true if the specified record exists; otherwise, false.
/// </returns>
public static boolean exist(recId   _recId)
{
    return _recId && (select firstonly RecId from eInvoiceJour_MX
        where eInvoiceJour_MX.RecId == _recId).RecId !=0;
}

/// <summary>
/// Finds the specified record in the <c>EInvoiceJour_MX</c> table by using the specified record ID.
/// </summary>
/// <param name="_recId">
/// The record ID of the <c>EInvoiceJour_MX</c> record to find.
/// </param>
/// <param name="_forUpdate">
/// A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name="_concurrencyModel">
/// The concurrency model option to use when reading the record; optional.
/// </param>
/// <returns>
/// A record in the <c>EInvoiceJour_MX</c> table; otherwise, an empty record.
/// </returns>
public static EInvoiceJour_MX find(
    recId               _recId,
    boolean             _forUpdate        = false,
    ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    EInvoiceJour_MX    electronicInvJour;

    if (_recId)
    {
        if (_forUpdate)
        {
            electronicInvJour.selectForUpdate(_forUpdate);
            electronicInvJour.concurrencyModel(_concurrencyModel);
        }

        select firstonly electronicInvJour
            where electronicInvJour.RecId == _recId;
    }

    return electronicInvJour;
}

/// <summary>
/// Finds the specified record in the <c>EInvoiceJour_MX</c> table by using the specified reference
/// fields.
/// </summary>
/// <param name="_refTableId">
/// The reference table ID of the <c>EInvoiceJour_MX</c> record to find.
/// </param>
/// <param name="_refRecId">
/// The record ID of the <c>EInvoiceJour_MX</c> record to find.
/// </param>
/// <param name="_forUpdate">
/// A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name="_concurrencyModel">
/// The concurrency model option to use when reading the record; optional.
/// </param>
/// <returns>
/// A record in the <c>EInvoiceJour_MX</c> table; otherwise, an empty record.
/// </returns>
public static EInvoiceJour_MX findByRef(
    RefTableId        _refTableId,
    RefRecId          _refRecId,
    boolean           _forUpdate        = false,
    ConcurrencyModel  _concurrencyModel = ConcurrencyModel::Auto)
{
    EInvoiceJour_MX    electronicInvJour;

    if (_refTableId && _refRecId)
    {
        if (_forUpdate)
        {
            electronicInvJour.selectForUpdate(_forUpdate);
            electronicInvJour.concurrencyModel(_concurrencyModel);
        }

        select firstonly electronicInvJour
            where electronicInvJour.RefTableId == _refTableId
               && electronicInvJour.RefRecId   == _refRecId;
    }

    return electronicInvJour;
}

/// <summary>
/// Sets the class variables based on Electronic Invoice Parameters.
/// </summary>
public void initFromElectronicInvoiceParameters()
{
    EInvoiceCFDIParameters_MX electrInvoiceParam = EInvoiceCFDIParameters_MX::find();

    this.CFDIVersion = electrInvoiceParam.CFDIVersion;

    this.UseAdvCertificateStore = SystemParameters::find().UseAdvCertificateStore;
    if(this.UseAdvCertificateStore && electrInvoiceParam.KeyVaultCertificateRef)
    {
        this.KeyVaultCertificateRef = electrInvoiceParam.KeyVaultCertificateRef;
        System.Security.Cryptography.X509Certificates.X509Certificate2 certificat = CertificateHelper_MX::getDigitalCertificate(this.UseAdvCertificateStore, electrInvoiceParam.KeyVaultCertificateRef, electrInvoiceParam.CertificateSubject);
        this.CertificateSubject = certificat.Subject;
    }
    else
    {
        this.CertificateSubject = electrInvoiceParam.CertificateSubject;
        this.KeyVaultCertificateRef = electrInvoiceParam.KeyVaultCertificateRef;
    }
}

/// <summary>
/// Gets the response description from the response XML of the current CFDI eInvoiceJour
/// </summary>
/// <returns>The response description of the CFDI request.</returns>
display EInvoiceCFDIResponseDescription_MX responseDescription()
{
    EInvoiceCFDIResponseDescription_MX responseDescription;

    if (!hasFieldAccess(tablenum(EInvoiceJour_MX),fieldnum(EInvoiceJour_MX, CFDIResponse)))
    {
        throw error("@SYS57330");
    }

    if (this.CFDIResponse)
    {
        EInvoiceXMLDocReader_MX xmlDocReader = EInvoiceXMLDocReader_MX::constructFromResponseMessage(this.CFDIVersion, this.CFDIResponse);
        responseDescription = xmlDocReader.getResponseDescription();
    }

    return responseDescription;
}

/// <summary>
/// Gets the response code from the response XML of the current CFDI eInvoiceJour
/// </summary>
/// <returns>The response code of the CFDI request.</returns>
display EInvoiceCFDIResponseCod_MX responseCod()
{
    EInvoiceCFDIResponseCod_MX responseCod;

    if (!hasFieldAccess(tablenum(EInvoiceJour_MX),fieldnum(EInvoiceJour_MX, CFDIResponse)))
    {
        throw error("@SYS57330");
    }

    if (this.CFDIResponse)
    {
        EInvoiceXMLDocReader_MX xmlDocReader = EInvoiceXMLDocReader_MX::constructFromResponseMessage(this.CFDIVersion, this.CFDIResponse);
        responseCod = xmlDocReader.getResponse();
    }

    return responseCod;
}

/// <summary>
/// Retrieves the source invoice.
/// </summary>
/// <returns>
/// The invoice table buffer.
/// </returns>
public Common invoiceJour()
{
    Common invoiceJour;

    switch (this.RefTableId)
    {
        case tableNum(CustInvoiceJour) :
            invoiceJour = CustInvoiceJour::findRecId(this.RefRecId);
            break;

        case tableNum(ProjInvoiceJour) :
            invoiceJour = ProjInvoiceJour::findRecId(this.RefRecId);
            break;

        case tableNum(RetailStatementJour):
            invoiceJour = RetailStatementJour::findRecId(this.RefRecId);
            break;

        case tableNum(RetailEInvoiceAggregatedStatement_MX):
            invoiceJour = RetailEInvoiceAggregatedStatement_MX::findRecId(this.RefRecId);
            break;
    }

    return invoiceJour;
}

/// <summary>
/// Finds the specified record in the <c>EInvoiceJour_MX</c> table by using the specified UUID.
/// </summary>
/// <param name="_UUID">
/// The CFDI UUID of the <c>EInvoiceJour_MX</c> record to find.
/// </param>
/// <param name="_forUpdate">
/// A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <returns>
/// A record in the <c>EInvoiceJour_MX</c> table; otherwise, an empty record.
/// </returns>
public static EInvoiceJour_MX findByUUID(
    EInvoiceCFDIUUID_MX  _UUID,
    boolean              _forUpdate = false)
{
    EInvoiceJour_MX    electronicInvJour;

    if (_UUID)
    {
        electronicInvJour.selectForUpdate(_forUpdate);

        select firstonly electronicInvJour
            where electronicInvJour.CFDIUUID == _UUID;
    }

    return electronicInvJour;
}

[SysObsolete('This method is deprecated use isRetailCFDIGlobalEInvoice instead.', false, 30\06\2020)]
public boolean isRetail()
{
    return this.isRetailCFDIGlobalEInvoice();
}

/// <summary>
/// Checks if the specified record exists in the <c>EInvoiceJour_MX</c> table by using the specified reference
/// fields.
/// </summary>
/// <param name="_refTableId">
/// The reference table ID of the <c>EInvoiceJour_MX</c> record to find.
/// </param>
/// <param name="_refRecId">
/// The record ID of the <c>EInvoiceJour_MX</c> record to find.
/// </param>
/// <returns>
/// True if record exists in the <c>EInvoiceJour_MX</c> table; otherwise, false.
/// </returns>
public static boolean existByRef(RefTableId _refTableId, RefRecId _refRecId)
{
    return
        (select RecId from EInvoiceJour_MX
            where EInvoiceJour_MX.RefTableId == _refTableId
                && EInvoiceJour_MX.RefRecId == _refRecId).RecId != 0;
}

/// <summary>
/// Checks if the specified record exists in the <c>EInvoiceJour_MX</c> table by using the specified reference and CFDI is Yes.
/// fields.
/// </summary>
/// <param name="_refTableId">
/// The reference table ID of the <c>EInvoiceJour_MX</c> record to find.
/// </param>
/// <param name="_refRecId">
/// The record ID of the <c>EInvoiceJour_MX</c> record to find.
/// </param>
/// <returns>
/// True if record exists in the <c>EInvoiceJour_MX</c> table; otherwise, false.
/// </returns>
public static boolean existCFDIByRef(RefTableId _refTableId, RefRecId _refRecId)
{
    return
        (select firstonly RecId from EInvoiceJour_MX
            where EInvoiceJour_MX.RefTableId == _refTableId
                && EInvoiceJour_MX.RefRecId == _refRecId
                && EInvoiceJour_MX.CFDI == NoYes::Yes).RecId != 0;
}

/// <summary>
/// Determinates if the current e-Invoice is a Retail CFDI Normal e-Invoice.
/// </summary>
/// <returns>true if the current e-Invoice is a Retail CFDI Normal e-Invoice; otherwise false.</returns>
public boolean isRetailCFDINormalEInvoice()
{
    boolean isRetailCFDINormal = false;

    if (this.RefTableId == tableNum(CustInvoiceJour))
    {
        CustInvoiceJour custInvoiceJour = this.invoiceJour();

        if (custInvoiceJour && RetailSalesTable::find(custInvoiceJour.SalesId).RetailOrder == NoYes::Yes)
        {
            isRetailCFDINormal = true;
        }
    }

    return isRetailCFDINormal;
}

/// <summary>
/// Determinates if the current e-Invoice is a Retail CFDI Global e-Invoice.
/// </summary>
/// <returns>true if the current e-Invoice is a Retail CFDI Global e-Invoice; otherwise false.</returns>
public boolean isRetailCFDIGlobalEInvoice()
{
    return this.RefTableId == tableNum(RetailStatementJour) || this.RefTableId == tableNum(RetailEInvoiceAggregatedStatement_MX);
}

/// <summary>
/// Determinates if the current e-Invoice is a Retail CFDI e-Invoice.
/// </summary>
/// <returns>true if the current e-Invoice is a Retail CFDI e-Invoice; otherwise false.</returns>
public boolean isRetailCFDIEInvoice()
{
    return (this.isRetailCFDIGlobalEInvoice() || this.isRetailCFDINormalEInvoice());
}
