public void initValue()
{
    CompanyInfo companyInfo = CompanyInfo::find();

    const str SDI10 = 'SDI10';

    super();

    this.Name = companyInfo.Name;
    this.CountryRegionId = companyInfo.postalAddress().CountryRegionId;
    this.CoRegNum = companyInfo.CoRegNum;
    this.FiscalCode_IT = companyInfo.FiscalCode_IT;
    this.Email = companyInfo.email();
    this.Phone = companyInfo.phone();
    this.TeleFax = companyInfo.teleFax();
    this.TrnTransmissionFormat = SDI10;
}

public void modifiedField(fieldId _fieldId)
{
    super(_fieldId);

    switch (_fieldId)
    {
        case fieldnum(EInvoiceParameters_IT, TrnIsFilingForSameLegalEntity):
            if (this.TrnIsFilingForSameLegalEntity)
            {
                this.TrnCountryCode = '';
                this.TrnTaxExempNumber = '';
                this.TrnEmail = '';
                this.TrnPhone = '';
            }
            break;

        case fieldnum(EInvoiceParameters_IT, IsTaxRep):
            if (!this.IsTaxRep)
            {
                this.TaxRepCountryCode = '';
                this.TaxRepTaxExempNumber = '';
                this.TaxRepFiscalCode = '';
                this.TaxRepIsCompany = NoYes::No;
                this.TaxRepCompanyDesignation = '';
                this.TaxRepFirstName = '';
                this.TaxRepLastName = '';
                this.TaxRepTitle = '';
                this.TaxRepCodeEORI = '';
            }
            break;

        case fieldnum(EInvoiceParameters_IT, TaxRepIsCompany):
            if (this.TaxRepIsCompany)
            {
                this.TaxRepFirstName = '';
                this.TaxRepLastName = '';
                this.TaxRepTitle = '';
            }
            else
            {
                this.TaxRepCompanyDesignation = '';
            }
            break;

        case fieldnum(EInvoiceParameters_IT, GenDigitalSignature):
            if (this.GenDigitalSignature == DigitalSignature::Disabled)
            {
                this.GenIsAutoDigitalSigning = NoYes::No;
            }

            this.validateEInvoiceSignerType();

            break;

        case fieldnum(EInvoiceParameters_IT, IsArt2250):
            if (!this.IsArt2250)
            {
                this.ArtProvince = '';
                this.ArtRegistrationNum = '';
                this.ArtShareCapital = '';
                this.ArtSoleShareholder = SoleShareholder_IT::Empty;
                this.ArtLiquidation = Liquidation_IT::NotSpecified;
            }
            break;

        case fieldNum(EInvoiceParameters_IT, EInvoiceSignerType_ES) :
            if (this.EInvoiceSignerType_ES != EInvoiceSignerType_ES::ThirdParty)
            {
                this.EInvoiceSignerParty_ES = 0;
            }

            this.validateEInvoiceSignerType();

            break;
    }
}

/// <summary>
/// Validates the signer type of the eInvoice.
/// </summary>
public void validateEInvoiceSignerType()
{
    #ISOCountryRegionCodes

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([ #isoES ])
        && (this.EInvoiceSignerType_ES == EInvoiceSignerType_ES::Issuer
        && this.GenDigitalSignature == DigitalSignature::Disabled))
    {
        warning("@AccountsReceivable:DigitalSignatureDisabledForEInvoices");
    }
}

public boolean validateWrite()
{
    #ISOCountryRegionCodes

    boolean ret = super();

    if (this.RecId && SysCountryRegionCode::isLegalEntityInCountryRegion([ #isoES ]))
    {
        switch (this.EInvoiceSignerType_ES)
        {
            case EInvoiceSignerType_ES::NotSpecified:
                ret = checkFailed(strFmt("@SYS84378", fieldPName(EInvoiceParameters_IT, EInvoiceSignerType_ES)));
                break;

            case EInvoiceSignerType_ES::ThirdParty:
                if (!this.EInvoiceSignerParty_ES)
                {
                    ret = checkFailed(strFmt("@SYS84378", fieldPName(EInvoiceParameters_IT, EInvoiceSignerParty_ES)));
                }
                break;
        }

        this.validateEInvoiceSignerType();
    }

    return ret;
}

public void insert()
{
    Global::handleEncryptedTablePreInsert(this);
    super();
    Global::handleEncryptedTablePostInsert(this);
}

public void update()
{
    Global::handleEncryptedTablePreUpdate(this);
