    Global::handleEncryptedTablePostUpdate(this);
    flush EInvoiceParameters_IT;
}

/// <summary>
/// Finds the Italian electronic invoice parameters record.
/// </summary>
/// <param name="_forupdate">
/// A Boolean value that indicates whether the record should be selected for update; optional.
/// </param>
/// <returns>
/// The Italian electronic invoice parameters record.
/// </returns>
public static EInvoiceParameters_IT find(boolean _forupdate = false)
{
    EInvoiceParameters_IT  parameter;

    if (_forupdate)
    {
        parameter.selectForUpdate(_forupdate);
    }

    select firstonly parameter
        index KeyIdx
        where parameter.Key == 0;

    if (!parameter && !parameter.isTmp())
    {
        Company::createParameter(parameter);
    }

    return parameter;
}

