/// <summary>
/// Get third party signer tax identification number.
/// </summary>
/// <returns>Third party signer tax identification number.</returns>
public str getThirdPartySignerTaxIdentificationNumber()
{
    str ret;

    CustTable custTable = CustTable::findByPartyRecId(this.EInvoiceSignerParty_ES);
    if (custTable)
    {
        ret = custTable.getPrimaryRegistrationNumber(TaxRegistrationTypesList::TAXID);
    }

    if (!ret)
    {
        VendTable vendTable = VendTable::findByPartyRecId(this.EInvoiceSignerParty_ES);
        if (vendTable)
        {
            ret = vendTable.getPrimaryRegistrationNumber(TaxRegistrationTypesList::TAXID);
        }
    }

    if (!ret)
    {
        CompanyInfo companyInfo = CompanyInfo::find();
        if (this.EInvoiceSignerParty_ES == companyInfo.RecId)
        {
            ret = companyInfo.CoRegNum;
        }
    }

    if (!ret)
    {
        ret = this.getTaxNumberByParty_ES();
    }

    return ret;
}

private str getTaxNumberByParty_ES()
{
    const str ESTaxId = 'TaxIdentificationNumber';
    const str emptyString = '';

    TaxRegistrationType taxRegistrationType = TaxRegistrationType::findByType(ESTaxId);
    if (!taxRegistrationType)
        return emptyString;

    LogisticsPostalAddress LogisticsPostalAddress = DirParty::primaryPostalAddress(this.EInvoiceSignerParty_ES);
    if (!LogisticsPostalAddress)
        return emptyString;

    DirPartyLocation dirPartyLocation = DirPartyLocation::findByPartyLocation(this.EInvoiceSignerParty_ES, LogisticsPostalAddress::getLocation(LogisticsPostalAddress.RecId));
    if (!dirPartyLocation)
        return emptyString;

    TaxRegistration taxRegistration = TaxRegistration::findDirPartyLocationOnDate(dirPartyLocation.RecId, taxRegistrationType.RecId);

    if (taxRegistration)
        return taxRegistration.RegistrationNumber;
    else
        return emptyString;
}

