/// <summary>
///    Indicates whether a record in the <c>EcoResInstanceValue</c> table exists.
/// </summary>
/// <param name="_recId">
///    The record ID of the record to check for existence.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
public static boolean exist(RefRecId _recId)
{
    return (EcoResInstanceValue::find(_recId).RecId != 0);
}

/// <summary>
///    Finds the specified record in the <c>EcoResInstanceValue</c> table.
/// </summary>
/// <param name="_recId">
///    The record ID of the record to find.
/// </param>
/// <param name="_forupdate">
///    A Boolean value that indicates whether the record should be read for update; optional.
/// </param>
/// <returns>
///    A record in the <c>EcoResInstanceValue</c> table; otherwise, an empty buffer.
/// </returns>
public static EcoResInstanceValue find(RefRecId _recId, boolean _forupdate = false)
{
    EcoResInstanceValue record;

    if (!_recId)
    {
        return record;
    }
    record.selectForUpdate(_forupdate);
    select firstonly record where record.RecId == _recId;
    return record;
}

