public EmplAdvTable_RU advTable(boolean _update = false)
{
    EmplAdvTable_RU    advTable;

    advTable.selectForUpdate(_update);

    select firstonly advTable
        where advTable.AdvanceId == this.AdvanceId;

    return advTable;
}

/// <summary>
///   Approved amount wiothout tax
/// </summary>
/// <returns>
///   Amount in transaction currency
/// </returns>
AmountCur amountExclTaxApproved()
{
    TaxEmplAdvance_RU taxSource;
    AmountCur lineAmount = this.AmountCurApproved;
    TaxAmount taxAmount;

    if (this.TaxIncluded)
    {
        taxSource  = new TaxEmplAdvance_RU(false, this, this.advTable(), null, true, this.RecId, false, false, true);
        taxAmount  = abs(taxSource.calcAndPost());
        lineAmount += (sign(lineAmount) == 1) ? -taxAmount : taxAmount;
    }

    return lineAmount;
}

public void calcTotal(boolean _updateAdvanceTable=true)
{
    EmplAdvTable_RU      advTable;
    TaxEmplAdvance_RU    taxSource;

    ttsbegin;

    if (! this.EmplTransRecId && EmplAdvLine_RU::findRecId(this.RecId))
    {
        advTable              = this.advTable();
        taxSource             = new TaxEmplAdvance_RU(false, this, advTable, null, true, this.RecId);
        taxSource.calculateTax();

        this.TaxAmount         = taxSource.taxAmount();
        this.TaxAmountMST      = taxSource.taxAmountMst();
        this.AmountCurExclTax  = taxSource.baseAmount();
        this.AmountMstExclTax  = taxSource.baseAmountMst();
        this.AmountMst         = this.AmountMstExclTax + this.TaxAmountMST;
        this.doUpdate();
    }

    if (_updateAdvanceTable)
    {
        EmplAdvTable_RU::recalculateAmountMst(this.AdvanceId);
    }

    ttscommit;
}

/// <summary>
/// Performs a budget check for the current Advance report line.
/// </summary>
/// <remarks>
/// Performs a check only if <c>SourceDocumentLine</c> field is filled.
/// </remarks>
public void checkBudgetOnDemand()
{
    if (this.SourceDocumentLine)
    {
        BudgetControlSourceDocumentProcessor::submitSourceDocumentLineToCheckBudget(
            SourceDocumentLine::find(this.SourceDocumentLine),
            true,  // is draft reservation
            null,  // caller
            true); // is on demand check
    }
}

private boolean checkExchRate()
{
    ExchangeRateHelper  exchangeRateHelper = ExchangeRateHelper::newCurrency(Ledger::current(), this.CurrencyCode);
    boolean             ret                = true;

    if (this.CurrencyCode == CompanyInfoHelper::standardCurrency())
    {
        if (this.ExchRate != 100.00)
        {
            ret = checkFailed(strFmt("@SYS85793", exchangeRateHelper.displayStoredExchangeRate(100)));
        }
    }

    return ret;
}

public void delete()
{
    EmplTrans_RU    linkedEmplTrans;

    ttsbegin;

    if (this.EmplTransRecId)
    {
        linkedEmplTrans           = EmplTrans_RU::findRecId(this.EmplTransRecId, true);
        linkedEmplTrans.AdvanceId = "";
        linkedEmplTrans.update();
    }

    TaxUncommitted::deleteForDocumentHeader(this.advTable().TableId, this.RecId, true);

    super();

    ttscommit;

    this.calcTotal();
}

