public void initExchRates(boolean _fetchEuroTriangulation = true)
{
    EmplAdvTable_RU    advTable = this.advTable();
    ;

    if (! this.EmplTransRecId)
    {
        if (_fetchEuroTriangulation)
        {
            this.Triangulation = Currency::triangulation(this.CurrencyCode, advTable.TransDate);
        }

        if (_fetchEuroTriangulation || this.Triangulation)
        {
            this.ExchRate       = ExchangeRateHelper::exchRate(this.CurrencyCode, advTable.TransDate, Currency::noYes2UnknownNoYes(this.Triangulation));
            this.ExchRateSecond = ExchangeRateHelper::exchRateSecond(this.CurrencyCode, advTable.TransDate, Currency::noYes2UnknownNoYes(this.Triangulation));
        }
        else
        {
            if (this.ExchRateSecond && this.ExchRate)
            {
                this.ExchRate = (this.ExchRateSecond / this.ExchRate) * 100;
            }

            this.ExchRateSecond = 0;
        }
    }
    else
    {
        this.ExchRate       = 0;
        this.ExchRateSecond = 0;
        this.Triangulation  = NoYes::No;
    }
}

