/// <summary>
///   The line can be distributed
/// </summary>
/// <returns>
///  true if can be
/// </returns>
boolean isDistributionEnabled()
{
    return this.LineType == EmplAdvLineType_RU::Expend ||
           this.LineType == EmplAdvLineType_RU::None;
}

/// <summary>
///  Decides, if distribution udate required
/// </summary>
/// <param name="_orig">
///  previous version of record
/// </param>
/// <returns>
///  true if update reuired
/// </returns>
public boolean isDistributionUpdateRequired(EmplAdvLine_RU _orig)
{
    boolean        recreateTax;
    boolean        deleteAllJournalLines;
    boolean        ret;

    if (! this.isDistributionEnabled())
    {
        return false;
    }

    recreateTax = this.isTaxRecreationRequired(_orig);
    if (recreateTax)
    {
        deleteAllJournalLines = true;
    }

    if ( this.AmountCur    != _orig.AmountCur ||
         this.Qty          != _orig.Qty       ||
         this.TaxIncluded  != _orig.TaxIncluded
        )
    {
        ret = true;
    }
    return ret;
}

/// <summary>
///   Copmpares data to _orig and decides whether deletion of distribution is required
/// </summary>
/// <param name="_orig">
///   Previous verison of line
/// </param>
/// <returns>
///   true if deleteion required, otherwise - false
/// </returns>
private boolean isLineDistributionDeletionRequired(EmplAdvLine_RU _orig)
{
    return this.DistributionTemplate  != _orig.DistributionTemplate ||
           (this.Qty                   != _orig.Qty && ! this.Qty)
    ;
}

/// <summary>
///  Decides whether tax recreatuon is required
/// </summary>
/// <param name="_orig">
///  previous version of line
/// </param>
/// <returns>
///   true if recreation is required, otherwise - false
/// </returns>
private boolean isTaxRecreationRequired(EmplAdvLine_RU _orig)
{
    return this.AmountCur    != _orig.AmountCur      ||
           this.Qty          != _orig.Qty            ||
           this.TaxGroup     != _orig.TaxGroup       ||
           this.TaxItemGroup != _orig.TaxItemGroup   ||
           this.TaxIncluded  != _orig.TaxIncluded    ||
           this.isLineDistributionDeletionRequired(_orig);
}

public AmountCur lineAmountCurInclTax()
{
    return this.TaxIncluded ?
            this.AmountCurApproved :
            this.AmountCurApproved + this.TaxAmount;
}

public AmountMST lineAmountMSTInclTax()
{
    return this.TaxIncluded ?
            this.AmountMst :
            this.AmountMst + this.TaxAmountMST;
}

