/// <summary>
/// Moves the document into draft status, unless it is already finalized or cancelled.
/// </summary>
public void resetDistribution_RU()
{
    SourceDocumentLineAccountingStatus accountingStatus = SourceDocumentLine::find(this.SourceDocumentLine).AccountingStatus;

    if (   accountingStatus == SourceDocumentLineAccountingStatus::Canceled
        || accountingStatus == SourceDocumentLineAccountingStatus::Finalized)
    {
        return;
    }

    if (accountingStatus == SourceDocumentLineAccountingStatus::Completed)
    {
        SourceDocumentLineProcessor::submitSourceDocumentLineImplementation(this,
            false,
            SourceDocumentLineAccountingStatus::FullyDistributed);
    }

    SourceDocumentLineProcessor::submitSourceDocumentLineImplementation(this,
        false,
        SourceDocumentLineAccountingStatus::Draft);
}

/// <summary>
/// Reset some informtaion when the current record is updated.
/// </summary>
public void setTouched()
{
    this.Touched = NoYes::Yes;
    this.SubLedgerJournalStatus = SubLedgerJournalStatus::Required;
}

public void update(boolean _updateAdvanceTable = true)
{
    DimensionDefault    dimensionOrig = this.orig().DefaultDimension;

    super();

    if (this.DefaultDimension != dimensionOrig)
    {
        this.resetDistribution_RU();
    }

    this.calcTotal(_updateAdvanceTable);

    if (this.isDistributionEnabled())
    {
        SourceDocumentProcessorFacade::submitSourceDocumentLineImplementation(this);
    }
}

public boolean validateField(FieldId _fieldIdToCheck)
{
    boolean ret;

    ret = super(_fieldIdToCheck);

    if (ret)
    {
        switch (_fieldIdToCheck)
        {
            case fieldNum(EmplAdvLine_RU, ExchRate) :
                ret = this.checkExchRate();
                break;
        }
    }
    return ret;
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if (this.advTable().TransDate < this.DocumentDate)
    {
        ret = checkFailed("@GLS104718");
    }

    if ((this.AmountCur * this.AmountCurApproved) < 0 || this.AmountCurApproved < 0)
    {
        ret = checkFailed(strFmt("@SYS77383", this.AmountCurApproved < 0 ? this.AmountCurApproved : this.AmountCur));
    }

    return ret;
}

public static boolean exist(EmplAdvanceId_RU _advanceId,
                            DocumentDate     _docDate,
                            DocumentNum      _docNum)
{
    return (select firstonly emplAdvLine_RU
                where emplAdvLine_RU.AdvanceId     == _advanceId   &&
                      emplAdvLine_RU.DocumentDate  == _docDate     &&
                      emplAdvLine_RU.DocumentNum   == _docNum).RecId != 0;
}

public static EmplAdvLine_RU find(EmplAdvanceId_RU _advanceId,
                                  DocumentDate     _docDate,
                                  DocumentNum      _docNum,
                                  boolean          _update = false)
{
    EmplAdvLine_RU advLine;
    ;

    advLine.selectForUpdate(_update);

    select firstonly advLine
        where advLine.AdvanceId     == _advanceId   &&
              advLine.DocumentDate  == _docDate     &&
              advLine.DocumentNum   == _docNum;

    return advLine;
}

public static EmplAdvLine_RU findLineNum(EmplAdvanceId_RU _advanceId,
                                                LineNum          _lineNum,
                                                boolean          _update = false)
{
    EmplAdvLine_RU advLine;
    ;

    if (_advanceId)
    {
        advLine.selectForUpdate(_update);
        select firstonly advLine
            where advLine.AdvanceId     == _advanceId   &&
                  advLine.LineNum       == _lineNum;
    }
    return advLine;
}

public static EmplAdvLine_RU findRecId(RecId    _recId,
                                              boolean  _update = false)
{
    EmplAdvLine_RU advLine;
    ;

    if (_recId)
    {
        advLine.selectForUpdate(_update);
        select firstonly advLine
            where advLine.RecId == _recId;
    }

    return advLine;
}

