/// <summary>
/// Gets rg23a payable opening balance.
/// </summary>
/// <param name="_transDate">
/// The transaction date.
/// </param>
/// <returns>
/// The instance of the <c>Amount</c> class.
/// </returns>
public Amount rg23aPayableOpeningBalance(TransDate _transDate)
{
    ExciseRG23ARegister_IN   exciseRG23ARegister;
    SecurityRights           sr;
    SecurityTableRights      stRights;
    AccessRight              ar;
    sr = SecurityRights::construct();
    stRights = sr.tableFieldAccessRights(tableStr(ExciseRG23ARegister_IN));
    ar = stRights.fieldAccessRight(fieldStr(ExciseRG23ARegister_IN,rg23aRecoverable));

    if (ar != AccessRight::NoAccess )
    {
        select sum(rg23aRecoverable),
               sum(rg23aPayable) from exciseRG23ARegister
            where exciseRG23ARegister.TaxRegistrationNumberTable == this.TaxRegistrationNumberTable &&
                  exciseRG23ARegister.rgDate                     < _transDate;
    }
    return  exciseRG23ARegister.rg23aRecoverable + exciseRG23ARegister.rg23aPayable;
}

/// <summary>
/// Gets excise rg23a available balance.
/// </summary>
/// <param name="_taxInquiryArguments">
/// The tax inquiry arguments.
/// </param>
/// <returns>
/// The instance of the <c>Amount</c> class.
/// </returns>
public static Amount exciseRG23AAvailBal(TaxInquiryArguments_IN   _taxInquiryArguments)
{
    ExciseRG23ARegister_IN          exciseRG23ARegister;
    ExciseRG23ARegister_IN          exciseRG23ARegister1;
    Amount                          availableBalance;
    TaxRegistrationNumbers_IN       taxRegistrationNumberTable;
    SecurityRights                  sr;
    SecurityTableRights             stRightsA;
    AccessRight                     arA;

    sr = SecurityRights::construct();
    stRightsA = sr.tableFieldAccessRights(tablestr(ExciseRG23ARegister_IN));
    arA = stRightsA.fieldAccessRight(fieldStr(ExciseRG23ARegister_IN, rg23aRecoverable));

    select sum(rg23aRecoverable) from exciseRG23ARegister
        join RecId from taxRegistrationNumberTable
        where taxRegistrationNumberTable.RegistrationType    == TaxRegistrationType_IN::Company
           && taxRegistrationNumberTable.TaxType             == TaxType_IN::Excise
           && taxRegistrationNumberTable.RegistrationNumber  == _taxInquiryArguments.getECCNumber()
           && exciseRG23ARegister.TaxRegistrationNumberTable == taxRegistrationNumberTable.RecId
           && exciseRG23ARegister.rgDate                     == _taxInquiryArguments.getFromDate();

    select sum(rg23aRecoverable) from exciseRG23ARegister1
        join RecId from taxRegistrationNumberTable
        where taxRegistrationNumberTable.RegistrationType     == TaxRegistrationType_IN::Company
           && taxRegistrationNumberTable.TaxType              == TaxType_IN::Excise
           && taxRegistrationNumberTable.RegistrationNumber   == _taxInquiryArguments.getECCNumber()
           && exciseRG23ARegister1.TaxRegistrationNumberTable == taxRegistrationNumberTable.RecId
           && exciseRG23ARegister1.rgDate                     < _taxInquiryArguments.getFromDate();

    if (arA != AccessRight::NoAccess  )
    {
        availableBalance = exciseRG23ARegister.rg23aRecoverable + exciseRG23ARegister1.rg23aRecoverable;
    }

    return availableBalance;
}

