/// <summary>
/// Reads the ExciseRG23ARegister_IN table for the specified id.
/// </summary>
/// <param name="_recId">
/// The unique record id to find.
/// </param>
/// <param name="_forUpdate">
/// If true, then read the record for updating.
/// </param>
/// <param name="_concurrencyModel">
/// An optional value used when the table is not automatically concurrent.
/// </param>
/// <returns>
/// A buffer containing ExciseRG23ARegister_IN table.
/// </returns>
public static ExciseRG23ARegister_IN find(
    RefRecId            _recId,
    boolean             _forUpdate = false,
    ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    ExciseRG23ARegister_IN    exciseRG23ARegister;

    exciseRG23ARegister.selectForUpdate(_forUpdate);
    if (_forUpdate && _concurrencyModel != ConcurrencyModel::Auto)
    {
        exciseRG23ARegister.concurrencyModel(_concurrencyModel);
    }

    select firstonly exciseRG23ARegister
        where exciseRG23ARegister.RecId == _recId;

    return exciseRG23ARegister;
}

/// <summary>
/// Gets excise rg23a register by ECC number rg date.
/// </summary>
/// <param name="_eccNumber">
/// The ECC number.
/// </param>
/// <param name="_date">
/// The date.
/// </param>
/// <param name="_taxCompomponent">
/// The tax component.
/// </param>
/// <param name="_exciseRecordType">
/// The excise record type.
/// </param>
/// <param name="_update">
/// The update flag.
/// </param>
/// <returns>
/// The instance of the <c>ExciseRG23ARegister_IN</c> class.
/// </returns>
public static ExciseRG23ARegister_IN findByEccNumberRgDate(
    RefRecId            _eccNumber,
    TransDate           _date,
    RefRecId            _taxCompomponent,
    ExciseRecordType_IN _exciseRecordType,
    boolean             _update = false)
{
    ExciseRG23ARegister_IN  exciseRG23ARegister;

    exciseRG23ARegister.selectForUpdate(_update);

    select firstonly exciseRG23ARegister
        where exciseRG23ARegister.TaxRegistrationNumberTable == _eccNumber       &&
              exciseRG23ARegister.rgDate                     == _date            &&
              exciseRG23ARegister.TaxComponentTable          == _taxCompomponent &&
              exciseRG23ARegister.ExciseRecordType           == _exciseRecordType;

    return exciseRG23ARegister;
}

