/// <summary>
/// Gets sum of rg23a payable.
/// </summary>
/// <param name="_eccNumber">
/// The ECC number.
/// </param>
/// <param name="_fromDate">
/// The from date.
/// </param>
/// <param name="_taxComp">
/// The tax component.
/// </param>
/// <returns>
/// The instance of the <c>Amount</c> class.
/// </returns>
public static Amount sumRG23APayable(
    RefRecId  _eccNumber,
    TransDate _fromDate,
    RefRecId  _taxComp = 0)
{
    ExciseRG23ARegister_IN      exciseRG23ARegister;
    SecurityRights              sr;
    SecurityTableRights         stRights;
    AccessRight                 ar;
    sr = SecurityRights::construct();
    stRights = sr.tableFieldAccessRights(tableStr(ExciseRG23ARegister_IN));
    ar = stRights.fieldAccessRight(fieldStr(ExciseRG23ARegister_IN,rg23aPayable));

    if (ar != AccessRight::NoAccess )
    {
        if (!_taxComp)
        {
            select sum(rg23aPayable) from exciseRG23ARegister
                where exciseRG23ARegister.TaxRegistrationNumberTable == _eccNumber &&
                      exciseRG23ARegister.rgDate                     < _fromDate;
        }
        else
        {
              select sum(rg23aPayable) from exciseRG23ARegister
                where exciseRG23ARegister.TaxComponentTable          == _taxComp
                   && exciseRG23ARegister.TaxRegistrationNumberTable == _eccNumber
                   && exciseRG23ARegister.rgDate                     <  _fromDate;
        }
    }
    return  exciseRG23ARegister.rg23aPayable;
}

