public edit ExchRate editExchRate(boolean _set, ExchRate _exchRate)
{
    ExchangeRateHelper  exchangeRateHelper = ExchangeRateHelper::newCurrency(
                                Ledger::primaryLedger(CompanyInfo::findDataArea(curext()).RecId),
                                FactureJour_RU::find(this.FactureId, this.Module).CurrencyCode);
    if (_set)
    {
        this.ExchRate = exchangeRateHelper.prepareExchangeRateForStorage(_exchRate);
    }

    return exchangeRateHelper.displayStoredExchangeRate(this.ExchRate);
}

public void initFromAdvanceLine(EmplAdvLine_RU _line)
{
    str itemName;

    this.FactureLineType     = FactureLineType_RU::AdvanceReport;
    this.InvoiceId           = _line.AdvanceId;
    this.InternalInvoiceId   = "";
    this.InvoiceDate         = _line.DocumentDate;
    this.SalesPurchId        = "";
    this.NumberSequenceGroup = "";
    this.InvoiceLineNum      = _line.LineNum;
    this.Unit                = "";
    this.DefaultDimension    = _line.DefaultDimension;
    this.InventTransId       = "";
    this.InventDimId         = "";
    this.ItemId              = "";
    this.InventGTDId         = "";
    this.CountryRegionName   = "";
    this.MarkupRefRecId      = _line.EmplTransRecId;
    this.MarkupRefTableId    = tableNum(EmplTrans_RU);

    itemName  = _line.DocumentNum;
    itemName += itemName && _line.DocumentName ? strFmt("@SYS26868", "", _line.DocumentName) : _line.DocumentName;
    itemName += itemName && _line.Description  ? strFmt("@SYS26868", "", _line.Description)  : _line.Description;
    this.ItemName            = itemName;
}

public void initFromInvoiceTrans(CustVendInvoiceTrans _trans)
{
    this.FactureLineType     = FactureLineType_RU::InvoiceLine;
    this.InvoiceId           = _trans.InvoiceNum;
    this.InvoiceDate         = _trans.InvoiceDate;
    this.SalesPurchId        = _trans.SalesPurchId_RU;
    this.NumberSequenceGroup = _trans.NumberSequenceGroupId_RU;
    this.InvoiceLineNum      = _trans.LineNum_RU;
    this.Unit                = _trans.Unit_RU;
    this.DefaultDimension    = _trans.DefaultDimension_RU;
    this.InventTransId       = _trans.InventTransId;
    this.ItemId              = _trans.ItemId;
    this.Category            = _trans.Category_RU;
    this.InventDimId         = _trans.InventDimId;
    this.InventGTDId         = InventDim::find(_trans.InventDimId).InventGtdId_RU;
    this.ItemName            = _trans.Name_RU;
    this.MarkupRefRecId      = 0;
    this.MarkupRefTableId    = 0;
}

public void initFromLedgerJournalTrans(LedgerJournalTrans  _trans)
{
    this.InvoiceId        = _trans.JournalNum;
    this.InvoiceDate      = _trans.TransDate;
    this.DefaultDimension = _trans.DefaultDimension ? _trans.DefaultDimension : LedgerDimensionFacade::getDefaultDimensionFromLedgerDimension(_trans.LedgerDimension);
    this.InvoiceLineNum   = _trans.LineNum;

    this.TaxGroup         = _trans.TaxGroup;
    this.TaxItemGroup     = _trans.TaxItemGroup;
    this.vatType          = FactureTrans_RU::taxType2VATType(TaxTable::find(_trans.TaxCode).TaxType_W);
    this.TaxCode          = (this.vatType == VATType_RU::Full    ||
                             this.vatType == VATType_RU::Reduced ||
                             this.vatType == VATType_RU::Zero) ?
                            _trans.TaxCode                     :
                            "";

    this.ExchRate         = _trans.ExchRate;
    this.MarkupRefRecId   = _trans.RecId;
    this.MarkupRefTableId = _trans.TableId;
}

private InventDim inventDim(boolean _update = false)
{
    return InventDim::find(this.InventDimId, _update);
}

display ItemNameDisplay itemNameDisplay()
{
    return this.ItemId                                               ?
           InventTable::find(this.ItemId).itemName(this.inventDim()) :
           (this.Category                            ?
            EcoResCategory::find(this.Category).Name :
            this.ItemName);
}

/// <summary>
/// Determines record id of original (first in chain of corrections) corrected facture trans.
/// </summary>
/// <returns>
/// Record id of original facture trans.
/// </returns>
public RefRecId refAdjustedTrans()
{
    CustVendInvoiceTrans    custVendInvoiceTrans;
    FactureTrans_RU         trans;
    RefRecId                refRecId = this.RecId;

    if (this.RefCorrectedFactureTrans)
    {
        trans = FactureTrans_RU::findRecId(this.RefCorrectedFactureTrans);
        switch (trans.Module)
        {
            case FactureModule_RU::Cust :
                custVendInvoiceTrans = CustInvoiceTrans::findFacture_RU(trans);
                break;
            case FactureModule_RU::Vend :
                custVendInvoiceTrans = VendInvoiceTrans::findFacture_RU(trans);
                break;
        }

        refRecId = custVendInvoiceTrans.CustVendInvoiceTrans::refAdjustedTrans_RU();
        select firstonly custVendInvoiceTrans
            where custVendInvoiceTrans.RecId == refRecId;

        switch (trans.Module)
        {
            case FactureModule_RU::Cust :
                refRecId = FactureTrans_RU::findCustInvoiceTrans(custVendInvoiceTrans).RecId;
                break;
            case FactureModule_RU::Vend :
                refRecId = FactureTrans_RU::findVendInvoiceTrans(custVendInvoiceTrans).RecId;
                break;
        }
    }

    return refRecId;
}

public static boolean existExtraLines(FactureModule_RU       _module,
                                             Num                    _salesPurchId,
                                             InvoiceId              _invoiceId,
                                             TransDate              _invoiceDate,
                                             NumberSequenceGroupId  _numberSeqGroup,
                                             PurchInternalInvoiceId _internalInvoiceId = "",
                                             FactureLineType_RU     _lineType          = FactureLineType_RU::InvoiceRoundOff)
{
    FactureTrans_RU trans;

    select firstonly RecId from trans
        where trans.Module              == _module             &&
              trans.FactureLineType     == _lineType           &&
              trans.InvoiceId           == _invoiceId          &&
              trans.InvoiceDate         == _invoiceDate        &&
              trans.SalesPurchId        == _salesPurchId       &&
              trans.NumberSequenceGroup == _numberSeqGroup     &&
              trans.InternalInvoiceId   == _internalInvoiceId;

    return trans.RecId;
}

/// <summary>
/// Method determines if facture has lines without VAT.
/// </summary>
/// <param name="_factureId">
/// Id of facture journal.
/// </param>
/// <param name="_module">
/// <c>FactureModule_RU</c> value of facture.
/// </param>
/// <returns>
/// True if facture contains lines without VAT tax; otherwise false.
/// </returns>
public static boolean existFactureNonVATTrans(FactureId_RU _factureId, FactureModule_RU _module)
{
    return _factureId
        && (select firstonly RecId from factureTrans_RU
            where factureTrans_RU.FactureId == _factureId
               && factureTrans_RU.Module    == _module
               && factureTrans_RU.vatType   == VATType_RU::NonLiable).RecId;
}

/// <summary>
/// Method determines if facture has lines with VAT.
/// </summary>
/// <param name="_factureId">
/// Id of facture journal.
/// </param>
/// <param name="_module">
/// <c>FactureModule_RU</c> value of facture.
/// </param>
/// <returns>
/// True if facture contains lines with VAT tax; otherwise false.
/// </returns>
public static boolean existFactureVATTrans(FactureId_RU _factureId, FactureModule_RU _module)
{
    return _factureId
        && (select firstonly RecId from factureTrans_RU
            where factureTrans_RU.FactureId == _factureId
               && factureTrans_RU.Module    == _module
               && factureTrans_RU.vatType   != VATType_RU::NonLiable).RecId;
}

/// <summary>
/// Searches facture trans record.
/// </summary>
/// <param name="_factureId">
/// Id of facture journal.
/// </param>
/// <param name="_module">
/// <c>FactureModule_RU</c> parameter.
/// </param>
/// <param name="_lineNum">
/// Line number of facture trans.
/// </param>
/// <param name="_update">
/// Should record be selected for update.
/// </param>
/// <returns>
/// Found <c>FactureTrans_RU</c> record
/// </returns>
static FactureTrans_RU find(FactureId_RU     _factureId,
                            FactureModule_RU _module,
                            LineNum          _lineNum,
                            boolean          _update = false)
{
    FactureTrans_RU factureTrans;
    ;

    factureTrans.selectForUpdate(_update);

    select firstonly factureTrans
        index hint FactureIdx
        where factureTrans.FactureId    == _factureId &&
              factureTrans.Module       == _module    &&
              factureTrans.LineNum      == _lineNum;

    return factureTrans;
}

/// <summary>
/// Searches <c>FactureTrans_RU</c> record by given <c>CustInvoiceTrans</c> record.
/// </summary>
/// <param name="_custInvoiceTrans">
/// <c>CustInvoiceTrans</c> record.
/// </param>
/// <param name="_forupdate">
/// Should record be selected for update.
/// </param>
/// <returns>
/// <c>FactureTrans_RU</c> record.
/// </returns>
public static FactureTrans_RU findCustInvoiceTrans(CustInvoiceTrans  _custInvoiceTrans,
                                                          boolean           _forupdate = false)
{
    FactureTrans_RU factureTrans;
    ;

    factureTrans.selectForUpdate(_forupdate);

    select firstonly factureTrans
        where factureTrans.InvoiceId            == _custInvoiceTrans.InvoiceId          &&
              factureTrans.InvoiceDate          == _custInvoiceTrans.InvoiceDate        &&
              factureTrans.InvoiceLineNum       == _custInvoiceTrans.LineNum            &&
              factureTrans.FactureLineType      == FactureLineType_RU::InvoiceLine      &&
              factureTrans.SalesPurchId         == _custInvoiceTrans.SalesId            &&
              factureTrans.Module               == FactureModule_RU::Cust               &&
              factureTrans.NumberSequenceGroup  == _custInvoiceTrans.NumberSequenceGroup;

    return factureTrans;
}

public static FactureTrans_RU findLedgerJournalTrans(InvoiceId _journal,
                                                     LineNum   _lineNum,
                                                     recId     _recId,
                                                     boolean   _recIdOnly = true)
{
    FactureTrans_RU trans;

    if (_recIdOnly)
    {
        select firstonly RecId from  trans
            where (trans.FactureLineType == FactureLineType_RU::TaxCorrection ||
                   trans.FactureLineType == FactureLineType_RU::Prepayment)   &&
                   trans.InvoiceId       == _journal                          &&
                   trans.InvoiceLineNum  == _lineNum                          &&
                   trans.MarkupRefRecId  == _recId;
    }
    else
    {
        select firstonly trans
            where (trans.FactureLineType == FactureLineType_RU::TaxCorrection ||
                   trans.FactureLineType == FactureLineType_RU::Prepayment)   &&
                   trans.InvoiceId       == _journal                          &&
                   trans.InvoiceLineNum  == _lineNum                          &&
                   trans.MarkupRefRecId  == _recId;
    }

    return trans;
}

static FactureTrans_RU findRecId(recId       recId,
                                 boolean     update = false )
{
    FactureTrans_RU factureTrans;
    ;

    factureTrans.selectForUpdate(update);

    select factureTrans
        where factureTrans.RecId== recId;

    return factureTrans;
}

public static FactureTrans_RU findVendInvoiceTrans(VendInvoiceTrans  _vendInvoiceTrans,
                                                          boolean           _forupdate = false)
{
    FactureTrans_RU factureTrans;
    ;

    factureTrans.selectForUpdate(_forupdate);

    select firstonly factureTrans
        where factureTrans.InvoiceId            == _vendInvoiceTrans.InvoiceId          &&
              factureTrans.InvoiceDate          == _vendInvoiceTrans.InvoiceDate        &&
              factureTrans.InvoiceLineNum       == _vendInvoiceTrans.LineNum            &&
              factureTrans.FactureLineType      == FactureLineType_RU::InvoiceLine      &&
              factureTrans.InternalInvoiceId    == _vendInvoiceTrans.InternalInvoiceId  &&
              factureTrans.SalesPurchId         == _vendInvoiceTrans.PurchID            &&
              factureTrans.Module               == FactureModule_RU::Vend               &&
              factureTrans.NumberSequenceGroup  == _vendInvoiceTrans.NumberSequenceGroup;

    return factureTrans;
}

public static VATType_RU taxType2VATType(TaxType_W _taxType)
{
    VATType_RU  ret;

    switch (_taxType)
    {
        case TaxType_W::VAT        :
            ret = VATType_RU::Full;
            break;
        case TaxType_W::VATReduced :
            ret = VATType_RU::Reduced;
            break;
        case TaxType_W::VATZero    :
            ret = VATType_RU::Zero;
            break;
        default                    :
            ret = VATType_RU::NonLiable;
            break;
    }

    return ret;
}

