static public int dimFieldListPosition(
    List        _dimFieldList,
    FieldId     _dimFieldId)
{
    ListEnumerator  enumerator  = _dimFieldList.getEnumerator();
    int             position    = 0;

    while (enumerator.moveNext())
    {
        position++;
        if (enumerator.current() == _dimFieldId)
        {
            return position;
        }
    }
    return 0;
}

