internal static void truncate()
{
    SQLDataDictionary sqlDict = new SQLDataDictionary();

    new SQLDataDictionaryPermission(methodstr(SQLDataDictionary, tableTruncate)).assert();
    sqlDict.tableTruncate(tableNum(InventInventoryDataServiceReservationQueue), false);
    CodeAccessPermission::revertAssert();
}

internal static void cleanProcessedQueueData(boolean _skipQueueLock, boolean _lockExist)
{
    if (!_skipQueueLock || !_lockExist)
    {
        InventInventoryDataServiceReservationQueue::truncate();
    }
    else
    {
        InventInventoryDataServiceQueueProcessHelper::deleteStatusReady("InventInventoryDataServiceReservationQueue");
    }
}

internal static void insertRecordsIntoTempTable(boolean _skipQueueLock, boolean _lockExist)
{
    InventInventoryDataServiceReservationQueueTemp  reservationQueueTemp;
    InventInventoryDataServiceReservationQueue      reservationQueue;

    if (_skipQueueLock)
    {
        if (_lockExist)
        {
            InventInventoryDataServiceQueueProcessHelper::updateStatusToReady("InventInventoryDataServiceReservationQueue");
        }
        else
        {
            update_recordset reservationQueue setting ProcessStatus = InventInventoryDataServiceQueueRecordStatus::Ready
                where reservationQueue.ProcessStatus == InventInventoryDataServiceQueueRecordStatus::New;
        }

        insert_recordset reservationQueueTemp (ItemId, InventDimId, Company, ReservationId, OffsetQty)
            select ItemId, InventDimId, Company, ReservationId, sum(OffsetQty)
                from reservationQueue
                group by ItemId, InventDimId, Company, ReservationId
                where reservationQueue.ProcessStatus == InventInventoryDataServiceQueueRecordStatus::Ready;
    }
    else
    {
        insert_recordset reservationQueueTemp (ItemId, InventDimId, Company, ReservationId, OffsetQty)
            select ItemId, InventDimId, Company, ReservationId, sum(OffsetQty)
                from reservationQueue
                group by ItemId, InventDimId, Company, ReservationId;
    }
}
