/// <summary>
/// Retrieves the <c>CompanyInfo</c> record associated with the current record.
/// </summary>
/// <returns>
/// The <c>CompanyInfo</c> record.
/// </returns>
public CompanyInfo companyInfo()
{
    return CompanyInfo::findDataArea(this.company());
}

/// <summary>
/// Retrieves a dimension set that can be applied to the <c>defaultDimension</c> field on this table.
/// </summary>
/// <param name="_defaultDimension">
/// A dimension set to apply to the <c>defaultDimension</c> field on this table.
/// </param>
/// <param name="_dimensionCopy">
/// A <c>dimensionCopy</c> object that was initialized by using the current buffer; optional.
/// </param>
/// <returns>
/// A dimension set that can be applied to the <c>defaultDimension</c> field on this table.
/// </returns>
/// <remarks>
/// This method makes sure that potential linked dimensions are not overwritten.
/// </remarks>

public DimensionDefault copyDimension(
    DimensionDefault _defaultDimension,
    DimensionCopy    _dimensionCopy = DimensionCopy::newFromTable(this,
                                                                  this.companyInfo().RecId
                                                                  )
    )
{
    return _dimensionCopy.copy(_defaultDimension);
}

/// <summary>
/// Initializes this <c>JmgIpcTrans</c> record based on a <c>JmgIpcJournalTrans</c> record.
/// </summary>
/// <param name="_jmgIpcJournalTrans">
/// A <c>JmgIpcJournalTrans</c> record that is used to initialize the <c>JmgIpcTrans</c> record.
/// </param>
public void initFromJmgIPCJournalTrans(JmgIpcJournalTrans _jmgIpcJournalTrans)
{
    this.Worker             = _jmgIpcJournalTrans.Worker;
    this.FromDateTime       = _jmgIpcJournalTrans.FromDateTime;
    this.ToDateTime         = _jmgIpcJournalTrans.ToDateTime;
    this.CategoryId         = _jmgIpcJournalTrans.IpcCategoryId;
    this.ActivityId         = _jmgIpcJournalTrans.IpcActivityId;
    this.DefaultDimension   = this.copyDimension(_jmgIpcJournalTrans.DefaultDimension);
    this.JournalId          = _jmgIpcJournalTrans.ipcJournalId;
    this.LineNum            = _jmgIpcJournalTrans.LineNum;
    this.Hours              = _jmgIpcJournalTrans.Hours;
}

/// <summary>
/// Retrieves a dimension set that contains the merged combination of the current
/// <c>defaultDimension</c> field on this table and the provided dimension set.
/// </summary>
/// <param name="_primaryDefaultDimension">
/// A first dimension set to merge with the current <c>defaultDimension</c> field on this table.
/// </param>
/// <param name="_secondaryDefaultDimension">
/// A second dimension set to merge with the current <c>defaultDimension</c> field on this table;
/// optional.
/// </param>
/// <param name="_dimensionMerge">
/// A <c>dimensionMerge</c> object that was initialized by using the current buffer; optional.
/// </param>
/// <returns>
/// A dimension set that contains the merged combination of the current <c>defaultDimension</c> field
/// on this table and the provided dimension set.
/// </returns>
/// <remarks>
/// This method makes sure that potential linked dimensions are not overwritten when they are merged.
/// </remarks>

public DimensionDefault mergeDimension(
    DimensionDefault _primaryDefaultDimension,
    DimensionDefault _secondaryDefaultDimension = 0,
    DimensionMerge   _dimensionMerge = DimensionMerge::newFromTable(this,
                                                                    this.companyInfo().RecId
                                                                    )
    )
{
    return _dimensionMerge.merge(_primaryDefaultDimension, _secondaryDefaultDimension);
}

/// <summary>
/// Determines if concurrent deletes should throw exception.
/// </summary>
/// <returns>true if exception should be thrown; otherwise false.</returns>
public boolean shouldThrowExceptionOnZeroDelete()
{
    return true;
}
