/// <summary>
    /// Copies the pay agreement lines from one day in a pay agreement to another day.
    /// </summary>
    /// <param name="to_PayAgreement">
    /// The ID of the pay agreement to copy the day to.
    /// </param>
    /// <param name="to_Day">
    /// The ID of the day to copy the pay agreement line to
    /// </param>
    /// <param name="doOverwrite">
    /// The <c>NoYes::Yes</c> enumeration value if an existing <c>JmgPayAgreementLine</c> record must be
    /// overwritten; otherwise, the <c>NoYes::No</c> value.
    /// </param>
    /// <param name="_paySpecType">
    /// The pay specification type of lines to copy. Only lines that have the specified pay specification
    /// type will be copied.
    /// </param>
    /// <returns>
    /// true if the copying succeeded; otherwise, false.
    /// </returns>
    boolean copyDay(JmgPayAgreement to_PayAgreement, JmgDayId to_Day, NoYesId doOverwrite, JmgPaySpecType _paySpecType)
    {
        JmgPayAgreementLine     jmgPayAgreementLine;
        JmgPayAgreementLine     copy;
        JmgPayLineDelimitation  jmgPayLineDelimitation;
        JmgPayLineDelimitation  delimitationCopy;
        ExpressionTable         expressionTable;

        if ((doOverwrite==NoYes::No) &&
            ((select firstonly jmgPayAgreementLine
                   where jmgPayAgreementLine.PayAgreement == to_PayAgreement    &&
                         jmgPayAgreementLine.DayId        == to_Day             &&
                         jmgPayAgreementLine.PaySpecType  == _paySpecType).RecId))
        {
            return false;
        }

        ttsbegin;

        delete_from jmgPayAgreementLine
            where jmgPayAgreementLine.PayAgreement == to_PayAgreement   &&
                  jmgPayAgreementLine.DayId        == to_Day            &&
                  jmgPayAgreementLine.PaySpecType  == _paySpecType;

        while select jmgPayAgreementLine
            where jmgPayAgreementLine.PayAgreement == this.PayAgreement &&
                  jmgPayAgreementLine.DayId        == this.DayId        &&
                  jmgPayAgreementLine.PaySpecType  == _paySpecType
        {
            copy.data(jmgPayAgreementLine);
            copy.PayAgreement = to_PayAgreement;
            copy.DayId        = to_Day;
            copy.insert();

            // Copy delimitations
            while select jmgPayLineDelimitation
                where jmgPayLineDelimitation.PayAgreementLineRecId  == jmgPayAgreementLine.RecId
                join  expressionTable
                where expressionTable.ExpressionId                  == jmgPayLineDelimitation.ExpressionId
            {
                delimitationCopy.data(jmgPayLineDelimitation);
                delimitationCopy.PayAgreementLineRecId  = copy.RecId;

                unchecked(Uncheck::TableSecurityPermission)
                {
                    delimitationCopy.ExpressionId           = expressionTable.copy().ExpressionId;
                }
                delimitationCopy.insert();
            }
        }

        ttscommit;

        return true ;
    }

/// <summary>
/// Retrieves the corrected end day of the pay agreement line.
/// </summary>
/// <returns>
/// the corrected end day of the pay agreement line.
/// </returns>
public display JmgLabelEndDay correctedEndDay()
{
    JmgDayId jmgDayId;

    jmgDayId = this.correctedStartDay();

    if (this.TimeTo < this.TimeFrom)
    {
        if (jmgDayId == JmgDayIdEnum::Sunday)
        {
            jmgDayId = JmgDayIdEnum::Monday;
        }
        else
        {
            jmgDayId = jmgDayId + 1;
        }
    }

    return jmgDayId;
}

/// <summary>
/// Retrieves the corrected start day of the pay agreement line.
/// </summary>
/// <returns>
/// the corrected start day of the pay agreement line.
/// </returns>
public display JmgLabelStartDay correctedStartDay()
{
    switch (this.ProfileStartCode)
    {
        case JmgProfileStartCode::Prev:
            if (this.DayId == JmgDayIdEnum::Monday)
            {
                return JmgDayIdEnum::Sunday;
            }
            return this.DayId - 1;
        case JmgProfileStartCode::Next:
            if (this.DayId == JmgDayIdEnum::Sunday)
            {
                return JmgDayIdEnum::Monday;
            }
            return this.DayId + 1;
    }
    return this.DayId;
}

/// <summary>
/// Gets the corrected start day of the pay agreement line as a String value.
/// </summary>
/// <returns>
/// The corrected start day of the pay agreement line.
/// </returns>
public display DateDayName correctedStartDayString()
{
    JmgDayIdEnum correctedDay = this.correctedStartDay();
    return enum2str(correctedDay);
}

