boolean validateWrite()
{
    boolean ret;

    if (ret && this.ForcePay == NoYes::Yes && !this.PayConst)
    {
        ret = checkFailed(strFmt("@SYS50104","@SYS8939"));
    }

    if (ret && this.FromDate && this.ToDate && this.FromDate > this.ToDate)
    {
        ret = checkFailed("@SYS16982");
    }

    if (this.ProjId != this.orig().ProjId && this.ActivityNumber == this.orig().ActivityNumber)
    {
        this.ActivityNumber = '';
    }

    ret = super();

    return ret;
}

