Method.Source:
/// <summary>
/// Update LedgerConsumptionTaxCalcTrans_JP table according to taxReport_JP record.
/// </summary>
/// <param name="_taxReport_JP">
/// _taxReport_JP record.
/// </param>
public void initFromTaxReport(TaxReport_JP _taxReport_JP)
{
    if (this.Version == TaxRepVersion::Original
        || this.Version == TaxRepVersion::Correct)
    {
        switch (_taxReport_JP.calcMethod())
        {
            case CalcMethod_JP::IndividualMethod:
                this.CalcItem17 = 0;
                break;

            case CalcMethod_JP::LumpsumMethod:
                this.CalcItem14 = 0;
                this.CalcItem15 = 0;
                this.CalcItem16 = 0;
                break;
        }
    }

    if (_taxReport_JP.amendment() == NoYes::Yes)
    {
        this.Apellative = CompanyInfo::find().CompanyRepresentative_JP;
        this.Version = TaxRepVersion::Original;
    }
}

/// <summary>
///    Initialize the TaxReportLayout value.
/// </summary>
public void initValue()
{
    super();

    this.TaxReportLayout= TaxReportLayout::Japan;
}

/// <summary>
///    Check whether version is more than 2.
/// </summary>
/// <returns>
/// If version is more than 2, return true.
/// </returns>
public boolean isVersionMatured()
{
    return !(this.Version == TaxRepVersion::Original || this.Version == TaxRepVersion::Correct);
}

/// <summary>
/// Modified field value.
/// </summary>
/// <param name="_fieldName">
/// The field name.
/// </param>
/// <param name="_arrayIndex">
/// The array index.
/// </param>
public void modifiedFieldValue(fieldName _fieldName, int _arrayIndex = 1)
{
    super(_fieldName, _arrayIndex);

    switch (_fieldName)
    {
        case fieldstr(LedgerConsumptionTaxCalcTrans_JP, CalcItem6)  :
            if (this.orig().CalcItem6 != this.CalcItem6)
            {
                this.Modified = NoYes::Yes;
            }
            break;
    }
}

/// <summary>
/// Reads the LedgerConsumptionTaxCalcTrans_JP table for the specified id.
/// </summary>
/// <param name="_recId">
/// The unique record id to find.
/// </param>
/// <param name="_forUpdate">
/// If true, then read the record for updating.
/// </param>
/// <param name="_concurrencyModel">
/// An optional value used when the table is not automatically concurrent.
/// </param>
/// <returns>
/// A buffer containing LedgerConsumptionTaxCalcTrans_JP table.
/// </returns>
public static LedgerConsumptionTaxCalcTrans_JP find(
    RefRecId            _recId,
    boolean             _forUpdate = false,
    ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    LedgerConsumptionTaxCalcTrans_JP ledgerConsumptionTaxCalcTrans_JP;

    ledgerConsumptionTaxCalcTrans_JP.selectForUpdate(_forupdate);
    if (_forupdate && _concurrencyModel != ConcurrencyModel::Auto)
    {
        ledgerConsumptionTaxCalcTrans_JP.concurrencyModel(_concurrencyModel);
    }

    select firstonly ledgerConsumptionTaxCalcTrans_JP
        where ledgerConsumptionTaxCalcTrans_JP.RecId == _recId;

    return ledgerConsumptionTaxCalcTrans_JP;
}

/// <summary>
/// Reads the LedgerConsumptionTaxCalcTrans_JP table for the specified details.
/// </summary>
/// <param name="_taxPeriod">
/// Tax period.
/// </param>
/// <param name="_fromDate">
/// Start date to find.
/// </param>
/// <param name="_toDate">
/// End date to find.
/// </param>
/// <param name="_typeOfDeclaration">
/// The detail info to find.
/// </param>
/// <param name="_forUpdate">
/// If true, then read the record for updating.
/// </param>
/// <returns>
/// A buffer containing LedgerConsumptionTaxCalcTrans_JP table.
/// </returns>
public static LedgerConsumptionTaxCalcTrans_JP findByDateAndTypeOfDeclaration(
    TaxPeriod            _taxPeriod,
    FromDate             _fromDate,
    ToDate               _toDate,
    TypeOfDeclaration_JP _typeOfDeclaration,
    boolean              _forUpdate = false)
{
    LedgerConsumptionTaxCalcTrans_JP ledgerConsumptionTaxCalcTrans_JP;

    if (_forUpdate)
    {
        ledgerConsumptionTaxCalcTrans_JP.selectForUpdate(_forUpdate);
    }

    select firstonly ledgerConsumptionTaxCalcTrans_JP
        where ledgerConsumptionTaxCalcTrans_JP.SettlementPeriod      == _taxPeriod
            && ledgerConsumptionTaxCalcTrans_JP.PeriodOfTaxationFrom == _fromDate
            && ledgerConsumptionTaxCalcTrans_JP.PeriodOfTaxationTo   == _toDate
            && ledgerConsumptionTaxCalcTrans_JP.TypeOfDeclaration    == _typeOfDeclaration
            && LedgerConsumptionTaxCalcTrans_JP.DataLayout == '';

    return ledgerConsumptionTaxCalcTrans_JP;
}

internal static LedgerConsumptionTaxCalcTrans_JP findByTaxReportParameters(TaxReportIParameters_JP _parameters, TaxReportDataLayout_JP _dataLayout, boolean _forUpdate = false)
{
    LedgerConsumptionTaxCalcTrans_JP ledgerConsumptionTaxCalcTrans;

    if (_forUpdate)
    {
        ledgerConsumptionTaxCalcTrans.selectForUpdate(_forUpdate);
    }

    if (_parameters)
    {
        select firstonly ledgerConsumptionTaxCalcTrans
            where ledgerConsumptionTaxCalcTrans.SettlementPeriod == _parameters.parmTaxPeriod()
                && ledgerConsumptionTaxCalcTrans.PeriodOfTaxationFrom == _parameters.parmFromDate()
                && ledgerConsumptionTaxCalcTrans.PeriodOfTaxationTo == _parameters.parmToDate()
                && ledgerConsumptionTaxCalcTrans.TypeOfDeclaration == _parameters.parmTypeOfDeclaration()
                && ledgerConsumptionTaxCalcTrans.DataLayout == _dataLayout;
    }

    return ledgerConsumptionTaxCalcTrans;
}

internal static boolean existByTaxReportParametersVersion(TaxReportIParameters_JP _parameters, TaxRepVersion _version, TaxReportDataLayout_JP _dataLayout)
{
    return _parameters != null && (select firstonly RecId from LedgerConsumptionTaxCalcTrans_JP
        where LedgerConsumptionTaxCalcTrans_JP.SettlementPeriod == _parameters.parmTaxPeriod()
            && LedgerConsumptionTaxCalcTrans_JP.PeriodOfTaxationFrom == _parameters.parmFromDate()
            && LedgerConsumptionTaxCalcTrans_JP.PeriodOfTaxationTo == _parameters.parmToDate()
            && LedgerConsumptionTaxCalcTrans_JP.TypeOfDeclaration == _parameters.parmTypeOfDeclaration()
            && LedgerConsumptionTaxCalcTrans_JP.DataLayout == _dataLayout
            && LedgerConsumptionTaxCalcTrans_JP.Version == _version).RecId != 0;
}

/// <summary>
/// Calculate the item 1 amount
/// </summary>
/// <returns>The item 1 amount</returns>
[SysClientCacheDataMethod(true)]
public display TaxOriginAmount_JP calcItem1()
{
    LedgerConsumptionTaxCalcTrans_JP ledgerConsumptionTaxCalcTrans;

    select sum(calcItem1) from ledgerConsumptionTaxCalcTrans
        where ledgerConsumptionTaxCalcTrans.SettlementPeriod == this.SettlementPeriod
            && ledgerConsumptionTaxCalcTrans.PeriodOfTaxationFrom == this.PeriodOfTaxationFrom
            && ledgerConsumptionTaxCalcTrans.PeriodOfTaxationTo == this.PeriodOfTaxationTo
            && ledgerConsumptionTaxCalcTrans.TypeOfDeclaration == this.TypeOfDeclaration;


    return ledgerConsumptionTaxCalcTrans.CalcItem1;
}

