/// <summary>
/// Calculate or enter the item 6 amount
/// </summary>
/// <param name = "Set"></param>
/// <param name = "_taxOriginAmount"></param>
/// <returns>The item 6 amount</returns>
[SysClientCacheDataMethod(true)]
public edit TaxOriginAmount_JP calcItem6(boolean Set, TaxOriginAmount_JP _taxOriginAmount)
{
    LedgerConsumptionTaxCalcTrans_JP ledgerConsumptionTaxCalcTrans;
    TaxAmount   taxAmount;

    if (Set)
    {
        this.CalcItem6 = _taxOriginAmount;
        taxAmount = _taxOriginAmount;
    }
    else
    {
        select sum(calcItem6) from ledgerConsumptionTaxCalcTrans
            where ledgerConsumptionTaxCalcTrans.SettlementPeriod == this.SettlementPeriod
                && ledgerConsumptionTaxCalcTrans.PeriodOfTaxationFrom == this.PeriodOfTaxationFrom
                && ledgerConsumptionTaxCalcTrans.PeriodOfTaxationTo == this.PeriodOfTaxationTo
                && ledgerConsumptionTaxCalcTrans.TypeOfDeclaration == this.TypeOfDeclaration;

        taxAmount = ledgerConsumptionTaxCalcTrans.CalcItem6;
    }

    return taxAmount;
}

