/// <summary>
/// Calculates the ratio of table sales.
/// </summary>
/// <returns>The ratio of table sales.</returns>
[SysClientCacheDataMethod(true)]
public display RatioOfTaxableSales_JP ratioOfTaxableSales()
{
    RatioOfTaxableSales_JP ratioOfTaxableSales;
    LedgerConsumptionTaxCalcTrans_JP ledgerConsumptionTaxCalcTrans;

    select sum(CalcItem7), sum(CalcItem4) from ledgerConsumptionTaxCalcTrans
        where ledgerConsumptionTaxCalcTrans.SettlementPeriod == this.SettlementPeriod
            && ledgerConsumptionTaxCalcTrans.PeriodOfTaxationFrom == this.PeriodOfTaxationFrom
            && ledgerConsumptionTaxCalcTrans.PeriodOfTaxationTo == this.PeriodOfTaxationTo
            && ledgerConsumptionTaxCalcTrans.TypeOfDeclaration == this.TypeOfDeclaration;

    if (ledgerConsumptionTaxCalcTrans.CalcItem7 != 0)
    {
        ratioOfTaxableSales = ledgerConsumptionTaxCalcTrans.CalcItem4 / ledgerConsumptionTaxCalcTrans.CalcItem7;
        ratioOfTaxableSales = round(ratioOfTaxableSales, 0.01);
    }

    return ratioOfTaxableSales;
}
