/// <summary>
/// Copies data from <c>SalesLine</c> table to <c>MCRHistSalesLine</c> table,
/// using the MCRHistSalesLineMap for all the fields that were mapped.
/// </summary>
/// <param name="_salesLine">
/// A record buffer of <c>SalesLine</c> table.
/// </param>
void createFromSalesLine(SalesLine  _salesLine)
{
    LogisticsPostalAddress logisticsPostalAddress;

    ttsbegin;
    this.clear();
    this.MCRHistSalesLineMap::initFromMCRHistSalesLineMap(_salesLine);

    //Delivery fields
    logisticsPostalAddress = LogisticsPostalAddress::findRecId(_salesLine.DeliveryPostalAddress);
    if (logisticsPostalAddress)
    {
        this.DeliveryAddress            = logisticsPostalAddress.Address;
        this.DeliveryCity               = logisticsPostalAddress.City;
        this.DeliveryCountryRegionId    = logisticsPostalAddress.CountryRegionId;
        this.DeliveryCounty             = logisticsPostalAddress.County;
        this.DeliveryState              = logisticsPostalAddress.State;
        this.DeliveryStreet             = logisticsPostalAddress.Street;
        this.DeliveryZipCode            = logisticsPostalAddress.ZipCode;
    }

    this.SalesLineRefRecId              = _salesLine.RecId;
    this.SalesQty                       = _salesLine.SalesQty;
    this.QtyOrdered                     = _salesLine.QtyOrdered;

    this.mcrSalesLineCreatedDateTime    = _salesLine.CreatedDateTime;

    if (this.validateWrite())
    {
        this.insert();
    }
    else
    {
        throw error("@SYS21628");
    }

    ttscommit;
}

/// <summary>
/// Calculates the sales quantity that has been delivered for a sales line.
/// </summary>
/// <returns>
/// The sales quantity that has been delivered.
/// </returns>
display SalesQty deliveredInTotal()
{
    SalesQty        qtySales = 0;

    if (!this.InventTransId)
    {
        qtySales    = 0;
    }
    else
    {
        if (this.InventTransId)
        {
            qtySales = (select sum(QtyPhysical) from custInvoiceTrans
                            index hint TransIdIdx
                            where custInvoiceTrans.InventTransId == this.InventTransId).QtyPhysical;

            qtySales += (select sum(Qty) from custPackingSlipTrans
                            index hint TransIdIdx
                            where custPackingSlipTrans.InventTransId == this.InventTransId).Qty;
        }
    }

    return qtySales;
}

