/// <summary>
/// Returns an image resource that indicates if the sales line price has been overriden.
/// </summary>
/// <returns>
/// An image resource identifier.
/// </returns>
display MCRDisplayPriceOverride mcrDisplayPriceOverride()
{
    #MCRresAppl;
    if (this.mcrPriceOverride == NoYes::Yes
        && this.LineDeliveryType != LineDeliveryType::DeliveryLine)
    {
        return #Image_PriceOverride;
    }
    else
    {
        return 0;
    }
}

