    public boolean validateWrite()
{
    boolean ret;

    ret = super();

    ret = ret && RetailInformationSubcodeTable::mcrValidate(this.RetailInfocodeId,this.mcrReasonCode);

    return ret;
}

/// <summary>
/// Finds the <c>MCRHistSalesLine</c> record.
/// </summary>
/// <param name="_transId">
/// An inventory transaction ID.
/// </param>
/// <param name="_update">
/// A <c>Boolean</c> value that indicates whether to update the record.
/// </param>
/// <returns>
/// A record buffer of <c>MCRHistSalesLine</c> table.
/// </returns>
static MCRHistSalesLine  findInventTransId(InventTransId _transId,
                                            boolean _update = false)
{
    MCRHistSalesLine mcrHistSalesLine;

    if (_transId)
    {
        if (_update)
        {
            mcrHistSalesLine.selectForUpdate(_update);
        }

        select firstonly mcrHistSalesLine
            index hint TransIdIdx
            where mcrHistSalesLine.InventTransId == _transId;
    }
    return mcrHistSalesLine;
}

/// <summary>
/// Finds the specified record in the <c>MCRHistSalesLine</c> table.
/// </summary>
/// <param name="_recId">
/// The record Id of the <c>MCRHistSalesLine</c> record to find.
/// </param>
/// <param name="update">
/// A <c>Boolean</c> value that indicates whether to update the record.
/// </param>
/// <returns>
/// A record in the <c>MCRHistSalesLine</c>; otherwise, an empty buffer.
/// </returns>
static MCRHistSalesLine findRecId(RecId     _recId,
                                 boolean    update = false)
{
    MCRHistSalesLine mcrHistSalesLine;

    if (_recId)
    {
        if (update)
        {
            mcrHistSalesLine.selectForUpdate(update);
        }

        select mcrHistSalesLine
            where mcrHistSalesLine.RecId == _recId;
    }

    return mcrHistSalesLine;
}
