/// <summary>
/// Displays the sum of all invoices for the original order.
/// </summary>
/// <returns>
/// The invoiced amount for the original order.
/// </returns>
public display AmountCur  amountInvoiced()
{
    CustInvoiceJour     custInvoiceJour;

    select sum(InvoiceAmount) from custInvoiceJour
        where custInvoiceJour.RefNum  == RefNum::SalesOrder
            && custInvoiceJour.SalesId == this.SalesId;
    return custInvoiceJour.InvoiceAmount;
}

/// <summary>
/// Uses the <c>MCRHistSalesTableMap</c> map to copy data from <c>salesTable</c> table to <c>MCRHistSalesTable</c> table
/// for all the fields that were mapped.
/// </summary>
/// <param name="_salesTable">
/// A record buffer of the <c>SalesTable</c> table.
/// </param>
public void createFromSalesTable(SalesTable _salesTable)
{
    LogisticsPostalAddress logisticsPostalAddress;

    this.clear();
    this.MCRHistSalesTableMap::initFromMCRHistSalesTableMap(_salesTable);

    //Delivery fields
    logisticsPostalAddress = LogisticsPostalAddress::findRecId(_salesTable.DeliveryPostalAddress);
    if (logisticsPostalAddress)
    {
        this.DeliveryAddress        = logisticsPostalAddress.Address;
        this.DeliveryCity           = logisticsPostalAddress.City;
        this.DeliveryCountryRegionId = logisticsPostalAddress.CountryRegionId;
        this.DeliveryCounty         = logisticsPostalAddress.County;
        this.DeliveryState          = logisticsPostalAddress.State;
        this.DeliveryStreet         = logisticsPostalAddress.Street;
        this.DeliveryZipCode        = logisticsPostalAddress.ZipCode;
    }

    this.SalesTableRefRecId             = _salesTable.RecId;

    this.mcrSalesTableCreatedDateTime   = _salesTable.CreatedDateTime;

    if (this.validateWrite())
    {
        this.insert();
    }
    else
    {
        throw error("@SYS21628" );
    }
}

/// <summary>
/// Finds the related <c>CustTable</c> record.
/// </summary>
/// <param name="_forUpdate">
/// A <c>boolean</c> used to determine whether or not a record should be selected for update; optional.
/// </param>
/// <returns>
/// The related <c>CustTable</c> record.
/// </returns>
public CustTable custTable_CustAccount(boolean _forUpdate = false)
{
    return CustTable::find(this.CustAccount, _forUpdate);
}

/// <summary>
/// Gets the customer delivery address.
/// </summary>
/// <returns>
/// The address found.
/// </returns>
/// <remarks>
/// The search order is customer's delivery address,
/// customer's billing address and order's delivery address.
/// </remarks>
public LogisticsPostalAddress deliveryAddress()
{
    CustTable               custTable;
    LogisticsPostalAddress  address;

    if (this.CustAccount)
    {
        custTable = this.custTable_CustAccount();
        address = custTable.mcrDefaultDeliveryPostalAddress();
        if (!address)
        {
            address = custTable.postalAddress();
        }
    }
    else
    {
        address= LogisticsPostalAddress::findRecId(this.DeliveryPostalAddress);
    }
    return address;
}

/// <summary>
/// Checks if any sales lines (History) have a negative quantity.
/// </summary>
/// <returns>
/// A <c>boolean</c> value that indicates whether the record was found.
/// </returns>
boolean mcrAnySalesLinesNegative()
{
    MCRHistSalesLine     mcrHistSalesLine;

    select firstonly mcrHistSalesLine
        where mcrHistSalesLine.SalesId                == this.SalesId
            &&    mcrHistSalesLine.LineDeliveryType   != LineDeliveryType::OrderLineWithMultipleDeliveries
            &&    mcrHistSalesLine.SalesStatus        != SalesStatus::Canceled
            &&    mcrHistSalesLine.SalesQty           <  0;

    if (mcrHistSalesLine)
    {
        return true;
    }
    else
    {
        return false;
    }
}

/// <summary>
/// Finds the <c>MCRHistSalesTable</c> record from the passed sales ID.
/// </summary>
/// <param name="_salesId">
/// The sales ID of the original order used to find the <c>MCRHistSalesTable</c> record.
/// </param>
/// <param name="_forUpdate">
/// A <c>boolean</c> used to determine whether or not a record should be selected for update; optional.
/// </param>
/// <param name="_concurrencyModel">
/// A <c>ConcurrencyModel</c> value that specifies the locking scheme; optional.
/// </param>
/// <returns>
/// A <c>MCRHistSalesTable</c> record.
/// </returns>
public static MCRHistSalesTable find(SalesId _salesId,
                             boolean _forUpdate = false,
                             ConcurrencyModel _concurrencyModel = ConcurrencyModel::Auto)
{
    MCRHistSalesTable mcrHistSalesTable;

    if (_salesId)
    {
        if (_forUpdate)
        {
            mcrHistSalesTable.selectForUpdate (_forUpdate);
            if (_concurrencyModel != ConcurrencyModel::Auto)
            {
                mcrHistSalesTable.concurrencyModel(_concurrencyModel);
            }
        }
        mcrHistSalesTable.selectLocked (_forUpdate);

        select firstonly mcrHistSalesTable
            index hint SalesIdx
            where mcrHistSalesTable.SalesId == _salesId;
    }

    return mcrHistSalesTable;
}

/// <summary>
/// Finds the <c>MCRHistSalesTable</c> record from the passed record ID.
/// </summary>
/// <param name="_recId">
/// The record ID of the original order used to find the <c>MCRHistSalesTable</c> record.
/// </param>
/// <returns>
/// A <c>MCRHistSalesTable</c> record.
/// </returns>
public static MCRHistSalesTable findRecId(RecId _recId)
{
    MCRHistSalesTable    mcrHistSalesTable;

    select mcrHistSalesTable
        where mcrHistSalesTable.RecId == _recId;

    return mcrHistSalesTable;
}
