/// <summary>
/// Initializes the order parameters table.
/// </summary>
/// <remarks>
/// Initialize the enum <c>MCRAuthContinuityChild</c> to NO. If set to yes and if
/// payment authorization set to online the system will try to online authorize
/// the credit card payment.
/// Initialize the retry on submit to NO.
/// If set to yes
/// If payment authorization is set to online
/// If reauthorization limit is greater than zero
/// then a declined credit card will be set to reauth when an order is submitted.
/// so the credit card can be retrieved.
/// </remarks>
void initValue()
{
    super();

    this.mcrAuthContinuityChild = NoYes::No;
    this.mcrRetryOnSubmit = NoYes::No;
    this.ShowDateFilterInCustomerService = NoYes::No;
    this.DefaultLookbackWindowCustomerService = 30;
}

void  insert()
{
    if (MCROrderParameters::exist())
    {
        throw error("@SYS22108");
    }

    super();
}

/// <summary>
///     Marks all FTC order events as inactive if the FTC processing flag was set to no.
/// </summary>
void update()
{
    MCROrderParameters   mcrOrderParameters_Orig = this.orig();

    super();

    if (  this.mcrftcProcessing == NoYes::No
        && mcrOrderParameters_Orig.mcrftcProcessing == NoYes::Yes)
    {
        MCROrderEventTable::markFTCEventsAsInactive(TableGroupAll::All);
    }

    flush MCROrderParameters;
}

boolean validateDelete()
{
    return false;
}

public boolean validateField(FieldId _fieldIdToCheck)
{
    boolean ret;

    ret = super(_fieldIdToCheck);

    switch (_fieldIdToCheck)
    {
        // Don't allow an invalid exchange source code
        case fieldNum(MCROrderParameters, mcrDefaultExchangeSourceID):

            if (this.mcrExchangeSourceCode == MCRExchangeSourceCode::DefaultSourceCode
               && this.mcrDefaultExchangeSourceID)
            {
                ret = MCRSourceCodeSetup::validateSourceEffective(this.mcrDefaultExchangeSourceID);
            }
            break;

        case fieldNum(MCROrderParameters, mcrSourceCodeRequired):

            if (this.mcrSourceCodeRequired
                && !MCROrderParameters::findCallCenterOrderParameters(fieldNum(RetailMCRChannelTable, mcrEnableDirectedSelling)))
            {
                ret = checkFailed("@MCR40357");
            }
            break;

        case fieldNum(MCROrderParameters, MCRReturnCreditPaymMode):

            RetailTenderFunction tenderTypeFunction = RetailTenderTypeTable::find(this.MCRReturnCreditPaymMode).defaultFunction;

            if (tenderTypeFunction != RetailTenderFunction::Normal &&
                tenderTypeFunction != RetailTenderFunction::Check &&
                tenderTypeFunction != RetailTenderFunction::Customer)
            {
                ret = checkFailed(strFmt("@MCR:UnsupportedMethodsOfPaymentsForAutomaticReturns", this.MCRReturnCreditPaymMode));
            }
            break;

        case fieldNum(MCROrderParameters, MCRApplyCreditsPaymMode):

            if (this.MCRApplyCreditsPaymMode)
            {
                RetailTenderFunction applyCreditsTenderType = RetailTenderTypeTable::find(this.MCRApplyCreditsPaymMode).defaultFunction;

                if (applyCreditsTenderType != RetailTenderFunction::Customer)
                {
                    ret = checkFailed(strFmt("@MCR:UnsupportedMethodsOfPaymentsForApplyCreditsReturns", this.MCRApplyCreditsPaymMode));
                }
            }
            break;
    }

    return ret;
}

/// <summary>
/// Checks if a <c>MCROrderParameters</c> record exists;
/// </summary>
/// <returns>
/// true if a <c>MCROrderParameters</c> record exists; otherwise, false.
/// </returns>
public static boolean exist()
{
    return (select firstonly RecId from mcrOrderParameters
        where mcrOrderParameters.Key == 0).RecId != 0;
}

/// <summary>
/// Finds the specified record in the <c>MCROrderParameters</c> table.
/// </summary>
/// <param name="_forupdate">
/// A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <returns>
/// A record in the <c>MCROrderParameters</c>; otherwise, an empty record.
/// </returns>
public static MCROrderParameters find(boolean _forupdate = false)
{
    MCROrderParameters mcrOrderParameters;

    if (!mcrOrderParameters.isTmp())
    {
        if (_forupdate)
        {
            mcrOrderParameters.selectForUpdate(_forupdate);
        }

        select firstonly mcrOrderParameters
        where mcrOrderParameters.Key == 0;

        if (!mcrOrderParameters)
        {
            if (mcrOrderParameters.validateWrite())
            {
                mcrOrderParameters.insert();
            }
        }
    }
    return mcrOrderParameters;
}

/// <summary>
/// Finds the value of the parameter on the channel if one exists for the current user, otherwise on the order parameters table.
/// </summary>
/// <param name="_fieldId">
/// The field ID of the parameter to retrieve either from the call center channel table or the order parameters table.
/// </param>
/// <param name="_channelRecId">
/// The channel record ID used to find the channel record.
/// </param>
/// <returns>
/// The value of the parameter on the channel if one exists for the current user, otherwise on the order parameters table.
/// </returns>
public static NoYes findCallCenterOrderParameters(FieldId _fieldId, RecId _channelRecId = 0)
{
    RetailMCRChannelTable       retailMCRChannelTable;
    NoYes                       ret;

    if (_channelRecId)
    {
        retailMCRChannelTable = RetailMCRChannelTable::findRecId(_channelRecId);
    }
    else
    {
        retailMCRChannelTable = RetailMCRChannelTable::findForCurrentUser();
    }

    if (retailMCRChannelTable)
    {
        switch (_fieldId)
        {
            case fieldNum(RetailMCRChannelTable, mcrEnableOrderCompletion):
                ret = retailMCRChannelTable.mcrEnableOrderCompletion;
                break;

            case fieldNum(RetailMCRChannelTable,mcrEnableDirectedSelling):
                ret = retailMCRChannelTable.mcrEnableDirectedSelling;
                break;

            case fieldNum(RetailMCRChannelTable,mcrEnableOrderPriceControl):
                ret = retailMCRChannelTable.mcrEnableOrderPriceControl;
                break;

            default:
                Error::wrongUseOfFunction(funcName());
        }
    }
    return ret;
}

/// <summary>
/// Indicates whether the user entered security code matches the security override code stored by the admin.
/// </summary>
/// <param name = "securityCode"> Security code entered by the user.</param>
/// <returns>True if passwords match; otherwise, false.</returns>
public static boolean isMatchSecurityOverrideCode(str securityCode)
{
    boolean ret = false;
    MCROrderParameters mcrOrderParameters = MCROrderParameters::find();

    if (MCROrderParameters::isCallCenterEnabledAndInUse() && mcrOrderParameters.SecurityOverrideCode != '')
    {
        ret = mcrOrderParameters.MCRAllowReturnPaymentOverride && (strCmp(mcrOrderParameters.SecurityOverrideCode, securityCode) == 0);
    }

    return ret;
}

/// <summary>
/// Checks if the current user has permissions to use alternate payments during return.
/// </summary>
/// <returns>True if the user is authorized to change return payments; otherwise, false.</returns>
public static boolean alternatePaymentsAllowedForUserRole()
{
    #Retail
    boolean ret = false;
    MCROrderParameters mcrOrderParameters = MCROrderParameters::find();

    if (MCROrderParameters::isCallCenterEnabledAndInUse())
    {
        MCRReturnOverride_PaymentMethod  returnOverride_PaymentMethod = new MCRReturnOverride_PaymentMethod();
        ret = mcrOrderParameters.MCRAllowReturnPaymentOverride && returnOverride_PaymentMethod.isUserRoleAuthorizedToOverrideReturnPayments(curUserId());
    }

    return ret;
}

/// <summary>
/// Indicates whether the call center functionality is enabled and being used for the current legal entity.
/// </summary>
/// <returns>
/// True, if call center configuration key is enabled and functionality is used; otherwise, false.
/// </returns>
public static boolean isCallCenterEnabledAndInUse()
{
    #Retail
    SysGlobalObjectCache c;
    container cachedValue;
    boolean ret = false;

    if (isConfigurationkeyEnabled(configurationKeyNum(MCRCallCenter)))
    {
        c = new SysGlobalObjectCache();
        cachedValue = c.find(#MCRCacheScope,[curext()]);
        if (cachedValue == conNull())
        {
            ret = (select firstonly RecId from retailChannelTable
                where retailChannelTable.ChannelType == RetailChannelType::MCRCallCenter
                   && retailChannelTable.InventLocationDataAreaId == curext()).RecId != 0;
            c.insert(#MCRCacheScope, [curext()], [ret]);
        }
        else
        {
            [ret] = cachedValue;
        }
    }

    return ret;
}

