public boolean invLocationEditable()
{
    boolean ret;

    switch (this.ReportType)
    {
        case OffReportType_RU::SalesInvoiceM15   :
        case OffReportType_RU::PurchM4M7         :
        case OffReportType_RU::TransferInvoiceM15:
            ret = (this.InvRelationType == OffInvRelationType_AllTable_RU::Table &&
                   (this.Position       == OffPosition_RU::Acceptor ||
                    this.Position       == OffPosition_RU::Supplier));
            break;
    }

    return ret;
}

