public boolean validateDelete()
{
    boolean                       ret = super();
    OfficialCheckPositionTrans_RU tmpcheckPosition;

    tmpcheckPosition = new OfficialCheckPositionTrans_RU(this.ReportType,
                                                         this.Position,
                                                         this.OffSessionId,
                                                         this.InvLocation,
                                                         this.RecId);

    ret = tmpcheckPosition.checkDelete() && ret;

    return ret;
}

public boolean validateField(FieldId _id)
{
    boolean ret = super(_id);

    switch (_id)
    {
        case fieldNum(OfficialsTrans_RU, CustVendRelation):
            ret = this.checkRelation(OffTableRelationsCheck_RU::CustVend)       && ret;
            break;

        case fieldNum(OfficialsTrans_RU, InvLocation):
            ret = this.checkRelation(OffTableRelationsCheck_RU::InventLocation) && ret;
            break;
    }

    return ret;
}

public boolean validateWrite()
{
    boolean                       ret = super();

    ret = this.checkRelation() && ret;

    OfficialCheckPositionTrans_RU checkPosition = new OfficialCheckPositionTrans_RU(this.ReportType,
                                                      this.Position,
                                                      this.OffSessionId,
                                                      this.InvLocation,
                                                      this.RecId);

    ret = checkPosition.check() && ret;

    if (this.Position != OffPosition_RU::InCharge &&
        this.Position != OffPosition_RU::Member)
    {
        OfficialsTrans_RU             officialsTrans;
        select firstonly RecId from officialsTrans
            where officialsTrans.ReportType       == this.ReportType       &&
                  officialsTrans.CustVendCode     == this.CustVendCode     &&
                  officialsTrans.CustVendRelation == this.CustVendRelation &&
                  officialsTrans.Position         == this.Position         &&
                  officialsTrans.OffSessionId     == this.OffSessionId     &&
                  officialsTrans.InvLocation      == this.InvLocation      &&
                  officialsTrans.RecId            != this.RecId;

        if (officialsTrans)
        {
            ret = checkFailed("@GLS222006");
        }
    }

    return ret;
}

public static void copySessionId(
    OffSessionId_RU  _offSessionID,
    OffSessionId_RU  _newSessionID,
    OffPosition_RU   _offPosition,
    OffReportType_RU _newReportType)
{
    OfficialsTrans_RU officialsTrans = OfficialsTrans_RU::findSessionId(_offSessionID, _offPosition);
    if (officialsTrans)
    {
        officialsTrans.OffSessionId = _newSessionID;
        officialsTrans.ReportType   = _newReportType;
        officialsTrans.insert();
    }
}

/// <summary>
/// Deletes the related officials report lines.
/// </summary>
/// <param name = "_sessionId">
/// The officials session ID.
/// </param>
public static void deleteWithoutUpdate(OffSessionId_RU _sessionId)
{
    if (OfficialsServ_RU::isFeatureEnabled())
    {
        OfficialsTrans_RU  officialsTrans;

        officialsTrans.skipDeleteMethod(true);
        delete_from officialsTrans
            where officialsTrans.OffSessionId == _sessionId;
    }
}

public static OfficialsTrans_RU find(
    OffSessionId_RU   _offSessionID,
    OffReportType_RU  _reportType,
    OffPosition_RU    _position,
    OffCustVendRel_RU _custvendRel    = "",
    InventLocationId  _inventLocation = "")
{
    OfficialsTrans_RU officialsTrans;

    // not firstonly intentionally
    select officialsTrans
        order by CustVendCode asc, InvRelationType desc
        where   officialsTrans.OffSessionId     == _offSessionID                         &&
                officialsTrans.ReportType       == _reportType                           &&
                officialsTrans.Position         == _position                             &&
              ((officialsTrans.CustVendCode     == OffTableAll_RU::Table                 &&
                officialsTrans.CustVendRelation == _custvendRel)    ||
                officialsTrans.CustVendCode     == OffTableAll_RU::All)                  &&
              ((officialsTrans.InvRelationType  == OffInvRelationType_AllTable_RU::Table &&
                officialsTrans.InvLocation      == _inventLocation) ||
                officialsTrans.InvRelationType  == OffInvRelationType_AllTable_RU::All);

    if (! officialsTrans &&
        ((_position     == OffPosition_RU::Director ||
          _position     == OffPosition_RU::Accountant)        &&
         (_reportType   != OffReportType_RU::VendFacture &&
          _reportType   != OffReportType_RU::PurchInvoice)))
    {
        select firstonly officialsTrans
            where officialsTrans.ReportType      == OffReportType_RU::General &&
                  officialsTrans.Position        == _position;
    }

    return officialsTrans;
}

public static OfficialsTrans_RU findFirstSessionPosition(
    OffSessionId_RU  _session,
    OffPosition_RU   _position,
    OffReportType_RU _reportType,
    boolean          _forUpdate = false)
{
    OfficialsTrans_RU trans;

    trans.selectForUpdate(_forUpdate);

    select firstonly trans
        where trans.ReportType   == _reportType &&
              trans.OffSessionId == _session    &&
              trans.Position     == _position;

    return trans;
}

public static OfficialsTrans_RU findFirstSessionPosition_InvLocation(
    OffSessionId_RU  _session,
    OffPosition_RU   _position,
    OffReportType_RU _reportType,
    InventLocationId _inventLocation = "",
    boolean          _forUpdate      = false)
{
    OfficialsTrans_RU   trans;

    trans.selectForUpdate(_forUpdate);

    select firstonly trans
        where trans.ReportType      == _reportType     &&
              trans.OffSessionId    == _session        &&
              trans.Position        == _position       &&
              trans.InvRelationType == OffInvRelationType_AllTable_RU::Table &&
              trans.InvLocation     == _inventLocation;

    if (! trans)
    {
       select firstonly trans
        where trans.ReportType      == _reportType     &&
              trans.OffSessionId    == _session        &&
              trans.Position        == _position       &&
              trans.InvRelationType == OffInvRelationType_AllTable_RU::All;
    }

    return trans;
}

#define.Any(0)
public static RecordSortedList findMulti(
    OffSessionId_RU   _offSessionID,
    OffReportType_RU  _reportType,
    OffPosition_RU    _position,
    OffCustVendRel_RU _custvendRel    = "",
    InventLocationId  _inventLocation = "",
    int               _maxQty         = #Any)
{
    OfficialsTrans_RU officialsTrans = OfficialsTrans_RU::find(_offSessionID,
                                                               _reportType,
                                                               _position,
                                                               _custvendRel,
                                                               _inventLocation);
    RecordSortedList  list = new RecordSortedList(tableNum(OfficialsTrans_RU));
    int               idx  = 1;

    list.sortOrder(fieldNum(OfficialsTrans_RU, RecId));

    while (officialsTrans && (_maxQty <= #Any || _maxQty >= idx))
    {
        list.ins(officialsTrans);

        next officialsTrans;
        idx ++;
    }

    return list;
}

public static OfficialsTrans_RU findSessionId(
    OffSessionId_RU _offSessionID,
    OffPosition_RU  _offPosition,
    boolean         _forupdate = false)
{
    OfficialsTrans_RU officialsTrans;

    officialsTrans.selectForUpdate(_forupdate);

    select firstonly officialsTrans
        where officialsTrans.OffSessionId == _offSessionID &&
              officialsTrans.Position     == _offPosition;

    return officialsTrans;
}

