public static void nameLookup(
    FormStringControl _ctrl,
    boolean           _isOurGuy,
    CustAccount       _custAccount = "",
    VendAccount       _vendAccount = "")
{
    SysTableLookup       sysTableLookup;
    Query                query = new Query();
    QueryBuildDataSource qbds;

    if (_isOurGuy)
    {
        sysTableLookup = SysTableLookup::newParameters(tableNum(DirPartyTable), _ctrl);
        sysTableLookup.addLookupfield (fieldNum      (DirPartyTable, Name));
        sysTableLookup.addLookupMethod(tableMethodStr(DirPartyTable, emplTitle_RU));

        qbds = query.addDataSource(tableNum(DirPartyTable));
        qbds.addSelectionField(fieldNum(DirPartyTable, Name));
        qbds.addSelectionField(fieldNum(DirPartyTable, RecId));

        qbds = qbds.addDataSource(tableNum(HcmWorker));
        qbds.joinMode(JoinMode::ExistsJoin);
        qbds.relations(true);
    }
    else
    {
        sysTableLookup = SysTableLookup::newParameters(tableNum(ContactPerson), _ctrl);
        sysTableLookup.addLookupMethod(tableMethodStr(ContactPerson, personName), true);
        sysTableLookup.addLookupfield (fieldNum(ContactPerson, Title));

        qbds = query.addDataSource(tableNum(ContactPerson));
        qbds.addSelectionField(fieldNum(ContactPerson, Title));
        qbds.addSelectionField(fieldNum(ContactPerson, Party));
        qbds.addRange(fieldNum(ContactPerson, Inactive)).value(SysQuery::value(NoYes::No));

        DirPartyRecId dirPartyRecId;
        if (_vendAccount)
        {
            dirPartyRecId = VendTable::find(_vendAccount).Party;
        }
        else if (_custAccount)
        {
            dirPartyRecId = CustTable::find(_custAccount).Party;
        }

        if (dirPartyRecId)
        {
            qbds.addRange(fieldNum(ContactPerson, ContactForParty)).value(queryValue(dirPartyRecId));
        }
    }

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

    public static void searchAccountIdx(OfficialsTrans_RU _search,
                                        OffReportType_RU  _reportType,
                                        OffSessionId_RU   _id,
                                        OffReportType_RU  _altReportType_1 = OffReportType_RU::General,
                                        OffReportType_RU  _altReportType_2 = OffReportType_RU::General)
    {
	    // not firstonly intentionally
        select _search
            order by InvLocation, Position
            where (_search.ReportType      == _reportType           ||
                   (_altReportType_1 != OffReportType_RU::General &&
                    _search.ReportType     == _altReportType_1)     ||
                   (_altReportType_2 != OffReportType_RU::General &&
                    _search.ReportType     == _altReportType_2))    &&
                   _search.OffSessionId    == _id;
    }

/// <summary>
/// Opens details form for the title of a person.
/// </summary>
/// <param name="_title">
/// Title of a person.
/// </param>
/// <param name="_isOurGuy">
/// Is the person internal contact (worker) ar an external contact (contact person).
/// </param>
/// <remarks>
/// Opens <c>HcmTitle</c> form for internal contacts (workers) and <c>PersonTitles</c> form for external contacts (contact persons).
/// Attempts to position cursor to the record matching current selection.
/// </remarks>
public static void titleJumpRefEx(
    OffTitleId_RU   _title,
    OffIsOurGuy_RU  _isOurGuy)
{
    Common          referencedRecord;
    str menuItemStr;

    if (_isOurGuy == OffIsOurGuy_RU::Yes)
    {
        menuItemStr = menuItemDisplayStr(HcmTitle);
        referencedRecord    = HcmTitle::findByTitle(_title);
    }
    else
    {
        menuItemStr = menuItemDisplayStr(PersonTitles);
        referencedRecord    = PersonTitleTable::find(_title);
    }

    MenuFunction menuFunction = new MenuFunction(menuItemStr, MenuItemType::Display);

    Args args = new Args();
    args.record(referencedRecord);

    FormRun formRun = menuFunction.create(args);
    formRun.run();
    if (referencedRecord)
    {
        FormDataSource dataSource = formRun.defaultDataSource() as FormDataSource;
        if (!dataSource)
        {
            dataSource = formRun.dataSource() as FormDataSource;
        }

        if (dataSource)
        {
            dataSource.positionToRecord(referencedRecord);
        }
    }
}

/// <summary>
/// Performs lookup for title of a person.
/// </summary>
/// <param name="_ctrl">
/// Control the lookup is linked to.
/// </param>
/// <param name="_isOurGuy">
/// Is the person internal contact (worker) ar an external contact (contact person).
/// </param>
/// <remarks>.
/// Uses <c>HcmTitle</c> table for internal contacts (workers) and <c>PersonTitleTable</c> table for external contacts (contact persons)
/// </remarks>
public static void titleLookupEx(
    FormStringControl   _ctrl,
    OffIsOurGuy_RU      _isOurGuy)
{
    TableId                 lookupTableId;
    FieldId                 lookupField,
                            lookupFieldAdditional;

    if (_isOurGuy == OffIsOurGuy_RU::Yes)
    {
        lookupTableId           = tableNum(HcmTitle);
        lookupField             = fieldNum(HcmTitle, TitleId);
    }
    else
    {
        lookupTableId           = tableNum(PersonTitleTable);
        lookupField             = fieldNum(PersonTitleTable, TitleId);
        lookupFieldAdditional   = fieldNum(PersonTitleTable, Title);
    }

    SysTableLookup sysTableLookup = SysTableLookup::newParameters(lookupTableId, _ctrl);

    sysTableLookup.addLookupfield(lookupField);
    if (lookupFieldAdditional)
    {
        sysTableLookup.addLookupfield(lookupFieldAdditional);
    }

    Query query = new Query();
    QueryBuildDataSource qbds = query.addDataSource(lookupTableId);
    qbds.addSortField(lookupField);

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}
