public abstract class PCPriceElement extends common
{
}

Method.Source:
/// <summary>
/// Accepts a product configuration model visitor.
/// </summary>
/// <param name="_visitor">
///  A product configuration model visitor.
/// </param>
public void accept(PCModelVisitor _visitor)
{
    PCPriceExpressionValue expressionValue;

    _visitor.visitStart(this);

    while select expressionValue
        where expressionValue.PriceElement == this.RecId
    {
        expressionValue.accept(_visitor);
    }

    _visitor.visitEnd(this);
}

/// <summary>
/// Checks whether the specified record is in the <c>PCPriceElement</c> table.
/// </summary>
/// <param name="_recId">
/// The record ID of the record to find.
/// </param>
/// <returns>
/// true if the specified record exists; otherwise, false.
/// </returns>
public static boolean exist(RefRecId _recId)
{
    return (PCPriceElement::find(_recId).RecId != 0);
}

/// <summary>
/// Finds the specified record in the <c>PCPriceElement</c> table.
/// </summary>
/// <param name="_recId">
/// The record ID of the record to find.
/// </param>
/// <param name="_forupdate">
/// A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <returns>
/// A record in the <c>PCPriceElement</c> table; otherwise, an empty record.
/// </returns>
public static PCPriceElement find(RefRecId _recId, boolean _forupdate = false)
{
    PCPriceElement record;

    if (!_recId)
    {
        return record;
    }
    record.selectForUpdate(_forupdate);
    select firstonly record where record.RecId == _recId;
    return record;
}

/// <summary>
/// Determines if concurrent deletes should throw exception.
/// </summary>
/// <returns>true if exception should be thrown; otherwise false.</returns>
public boolean shouldThrowExceptionOnZeroDelete()
{
    return true;
}
